    } catch (error: any) {
      console.error("Convert links to products error:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  // Generate blogs from product categories
  app.post('/api/automation/generate-category-blogs', async (req, res) => {
    try {
      const { contentWorkflow } = await import('./automation/contentWorkflow');

      const result = await contentWorkflow.runCompleteWorkflow({
        processUnprocessedLinks: false,
        createProducts: false,
        createBlogs: true
      });

      res.json({
        success: result.success,
        data: {
          blogsCreated: result.blogsCreated,
          errors: result.errors
        }
      });
    } catch (error: any) {
      console.error("Generate category blogs error:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  // Advanced Analytics Routes
  app.get('/api/analytics/wellness-insights', async (req, res) => {
    try {
      const { wellnessAnalytics } = await import('./analytics/wellnessAnalytics');
      const timeRange = (req.query.timeRange as string) || '30d';

      console.log(`üîç Generating wellness insights for timeRange: ${timeRange}`);
      const insights = await wellnessAnalytics.generateWellnessInsights(timeRange as any);

      res.json({
        success: true,
        data: insights,
        generatedAt: new Date().toISOString(),
        timeRange
      });
    } catch (error: any) {
      console.error("Analytics insights error:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  // Real-time analytics metrics
  app.get('/api/analytics/real-time', async (req, res) => {
    try {
      // Get real-time metrics (simplified for demo)
      const realTimeData = {
        activeUsers: Math.floor(Math.random() * 50) + 20,
        currentSessions: Math.floor(Math.random() * 30) + 10,
        revenueToday: Math.floor(Math.random() * 1000) + 500,
        contentViews: Math.floor(Math.random() * 200) + 100,
        newSignups: Math.floor(Math.random() * 10) + 2,
        challengeCompletions: Math.floor(Math.random() * 25) + 5,
        lastUpdated: new Date().toISOString()
      };

      res.json({
        success: true,
        data: realTimeData
      });
    } catch (error: any) {
      console.error("Real-time analytics error:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  // Export analytics data
  app.post('/api/analytics/export', async (req, res) => {
    try {
      const { format, timeRange, sections } = req.body;

      // Simulate export process
      await new Promise(resolve => setTimeout(resolve, 1000));

      res.json({
        success: true,
        message: `Analytics data exported successfully as ${format}`,
        downloadUrl: `/downloads/analytics-${Date.now()}.${format}`,
        exportedAt: new Date().toISOString(),
        sections: sections || ['all']
      });
    } catch (error: any) {
      console.error("Analytics export error:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  // Autonomous System Control Routes
  app.post('/api/automation/autonomous/start', async (req, res) => {
    try {
      const { autonomousController } = await import('./automation/autonomousController');
      const result = await autonomousController.startAutonomousMode();

      res.json({
        success: result.success,
        message: result.message,
        timestamp: new Date().toISOString()
      });
    } catch (error: any) {
      console.error("Failed to start autonomous mode:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  app.post('/api/automation/autonomous/stop', async (req, res) => {
    try {
      const { autonomousController } = await import('./automation/autonomousController');
      const result = await autonomousController.stopAutonomousMode();

      res.json({
        success: result.success,
        message: result.message,
        timestamp: new Date().toISOString()
      });
    } catch (error: any) {
      console.error("Failed to stop autonomous mode:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  app.get('/api/automation/autonomous/status', async (req, res) => {
    try {
      const { autonomousController } = await import('./automation/autonomousController');
      const status = autonomousController.getStatus();
      const health = await autonomousController.getSystemHealth();

      res.json({
        success: true,
        data: {
          ...status,
          systemHealth: health
        }
      });
    } catch (error: any) {
      console.error("Failed to get autonomous status:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  app.put('/api/automation/autonomous/config', async (req, res) => {
    try {
      const { autonomousController } = await import('./automation/autonomousController');
      await autonomousController.updateConfig(req.body);

      res.json({
        success: true,
        message: 'Configuration updated successfully',
        timestamp: new Date().toISOString()
      });
    } catch (error: any) {
      console.error("Failed to update autonomous config:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  // Intelligent Scheduler Routes
  app.post('/api/automation/scheduler/start', async (req, res) => {
    try {
      const { intelligentScheduler } = await import('./automation/intelligentScheduler');
      await intelligentScheduler.startScheduler();

      res.json({
        success: true,
        message: 'Intelligent scheduler started',
        timestamp: new Date().toISOString()
      });
    } catch (error: any) {
      console.error("Failed to start scheduler:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  app.post('/api/automation/scheduler/stop', async (req, res) => {
    try {
      const { intelligentScheduler } = await import('./automation/intelligentScheduler');
      await intelligentScheduler.stopScheduler();

      res.json({
        success: true,
        message: 'Intelligent scheduler stopped',
        timestamp: new Date().toISOString()
      });
    } catch (error: any) {
      console.error("Failed to stop scheduler:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  app.get('/api/automation/scheduler/status', async (req, res) => {
    try {
      const { intelligentScheduler } = await import('./automation/intelligentScheduler');
      const status = intelligentScheduler.getSchedulerStatus();

      res.json({
        success: true,
        data: status
      });
    } catch (error: any) {
      console.error("Failed to get scheduler status:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  app.post('/api/automation/scheduler/schedule-task', async (req, res) => {
    try {
      const { intelligentScheduler } = await import('./automation/intelligentScheduler');
      const taskId = await intelligentScheduler.scheduleTask(req.body);

      res.json({
        success: true,
        data: { taskId },
        message: 'Task scheduled successfully'
      });
    } catch (error: any) {
      console.error("Failed to schedule task:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  app.get('/api/automation/scheduler/tasks', async (req, res) => {
    try {
      const { intelligentScheduler } = await import('./automation/intelligentScheduler');
      const tasks = await intelligentScheduler.getScheduledTasks();

      res.json({
        success: true,
        data: tasks
      });
    } catch (error: any) {
      console.error("Failed to get scheduled tasks:", error);
      res.status(500).json({
        success: false,
        error: error.message
      });
    }
  });

  // ============== AGENT MANAGEMENT ENDPOINTS ==============

  // Get agent system status and metrics
  app.get('/api/agents/status', isAuthenticated, asyncHandler(async (req, res) => {
    const systemMetrics = await storage.getSystemMetrics();
    const agentStats = await storage.getAgentStats();

    sendSuccess(res, {
      autonomyLevel: systemMetrics.autonomyLevel,
      systemHealth: systemMetrics.systemHealth,
      uptime: systemMetrics.uptime,
      agents: {
        totalTasks: agentStats.totalTasks,
        pendingTasks: agentStats.pendingTasks,
        completedTasks: agentStats.completedTasks,
        failedTasks: agentStats.failedTasks,
        successRate: agentStats.successRate
      },
      revenue: {
        total: systemMetrics.totalRevenue,
        contentGenerated: systemMetrics.contentGenerated
      },
      lastUpdated: systemMetrics.lastUpdated
    });
  }));

  // Create new agent task
  app.post('/api/agents/task', isAuthenticated, requireAdmin, asyncHandler(async (req, res) => {
    const { type, priority, parameters, estimatedDuration } = req.body;

    if (!type || !priority) {
      return sendError(res, 'Task type and priority are required', 400);
    }

    const task = await storage.createAgentTask({
      type,
      priority,
      parameters,
      estimatedDuration: estimatedDuration || 15,
      scheduledFor: new Date()
    });

    sendSuccess(res, task, 'Task created successfully');
  }));

  // Get agent task history
  app.get('/api/agents/history', isAuthenticated, asyncHandler(async (req, res) => {
    const { status, limit = 50 } = req.query;
    const tasks = await storage.getAgentTasks(status as string);

    const limitedTasks = tasks.slice(0, parseInt(limit as string) || 50);
    sendSuccess(res, limitedTasks);
  }));

  // Update agent task status
  app.patch('/api/agents/task/:id', isAuthenticated, requireAdmin, asyncHandler(async (req, res) => {
    const { id } = req.params;
    const updates = req.body;

    const updatedTask = await storage.updateAgentTask(parseInt(id), updates);
    if (!updatedTask) {
      return sendError(res, 'Task not found', 404);
    }

    sendSuccess(res, updatedTask, 'Task updated successfully');
  }));

  // ============== AUTONOMOUS SYSTEM ENDPOINTS ==============

  // Start autonomous foundation (Week 1 upgrade)
  app.post('/api/automation/autonomous/start', isAuthenticated, requireAdmin, asyncHandler(async (req, res) => {
    try {
      const { autonomousFoundation } = await import('./automation/autonomousFoundation');
      const result = await autonomousFoundation.activateFoundation();

      if (result.success) {
        sendSuccess(res, { 
          autonomousMode: true, 
          message: result.message,
          autonomyLevel: result.autonomyLevel,
          weekPhase: 'Foundation (Week 1)'
        });
      } else {
        sendError(res, result.message, 400);
      }
    } catch (error) {
      console.error('Error starting autonomous foundation:', error);
      sendError(res, 'Failed to start autonomous foundation', 500);
    }
  }));

  // Stop autonomous foundation
  app.post('/api/automation/autonomous/stop', isAuthenticated, requireAdmin, asyncHandler(async (req, res) => {
    try {
      const { autonomousFoundation } = await import('./automation/autonomousFoundation');
      const result = await autonomousFoundation.deactivateFoundation();

      if (result.success) {
        sendSuccess(res, { 
          autonomousMode: false, 
          message: result.message,
          weekPhase: 'Stopped'
        });
      } else {
        sendError(res, result.message, 400);
      }
    } catch (error) {
      console.error('Error stopping autonomous foundation:', error);
      sendError(res, 'Failed to stop autonomous foundation', 500);
    }
  }));

  // Get autonomous foundation status and metrics
  app.get('/api/automation/autonomous/metrics', isAuthenticated, asyncHandler(async (req, res) => {
    try {
      const { autonomousFoundation } = await import('./automation/autonomousFoundation');
      const foundationStatus = await autonomousFoundation.getFoundationStatus();
      const systemMetrics = await storage.getSystemMetrics();

      sendSuccess(res, {
        ...systemMetrics,
        foundation: foundationStatus,
        weekPhase: foundationStatus.isActive ? 'Foundation (Week 1)' : 'Inactive',
        targetAutonomy: foundationStatus.isActive ? '60%' : 'N/A'
      });
    } catch (error) {
      console.error('Error fetching autonomous metrics:', error);
      const fallbackMetrics = await storage.getSystemMetrics();
      sendSuccess(res, fallbackMetrics);
    }
  }));

  // ============== REVENUE TRACKING ENDPOINTS ==============

  // Get revenue analytics
  app.get('/api/revenue/analytics', isAuthenticated, requireAdmin, asyncHandler(async (req, res) => {
    const revenueStats = await storage.getRevenueStats();
    const contentStats = await storage.getContentEngagementStats();

    sendSuccess(res, {
      revenue: revenueStats,
      content: contentStats,
      performance: {
        revenuePerContent: revenueStats.totalRevenue / (contentStats.totalContent || 1),
        contentProductivity: contentStats.totalContent,
        engagementRate: 0.75 // Placeholder - implement actual engagement tracking
      }
    });
  }));

  // Track revenue event
  app.post('/api/revenue/track', asyncHandler(async (req, res) => {
    const { amount, source, productId, affiliateId } = req.body;

    if (!amount || !source) {
      return sendError(res, 'Amount and source are required', 400);
    }

    const tracking = await storage.createRevenueTracking({
      amount,
      source,
      productId,
      affiliateId,
      timestamp: new Date()
    });

    sendSuccess(res, tracking, 'Revenue tracked successfully');
  }));

  // ============== SOCIAL MEDIA ENDPOINTS ==============

  // Publish to social media
  app.post('/api/social/publish', isAuthenticated, requireAdmin, asyncHandler(async (req, res) => {
    const { platform, content, imageUrl, scheduledFor } = req.body;

    if (!platform || !content) {
      return sendError(res, 'Platform and content are required', 400);
    }

    // Create social publishing task
    const task = await storage.createAgentTask({
      type: 'SOCIAL_PUBLISH',
      priority: 'MEDIUM',
      parameters: { platform, content, imageUrl, scheduledFor },
      scheduledFor: scheduledFor ? new Date(scheduledFor) : new Date()
    });

    sendSuccess(res, task, 'Social media post scheduled successfully');
  }));

  // Get social media stats
  app.get('/api/social/stats', isAuthenticated, asyncHandler(async (req, res) => {
    // Placeholder implementation - integrate with actual social media APIs
    const stats = {
      platforms: {
        twitter: { followers: 1250, posts: 45, engagement: 3.2 },
        instagram: { followers: 2800, posts: 32, engagement: 4.1 },
        tiktok: { followers: 950, posts: 18, engagement: 5.8 }
      },
      totalReach: 5000,
      totalEngagement: 4.2
    };

    sendSuccess(res, stats);
  }));

  // ============== ADVANCED ANALYTICS ENDPOINTS ==============

  // Get user wellness insights
  app.get('/api/analytics/wellness/user', isAuthenticated, asyncHandler(async (req, res) => {
    const userId = (req.user as any).claims.sub;
    const { timeframe = '30' } = req.query;

    try {
      const { wellnessAnalytics } = await import('./analytics/wellnessAnalytics');
      const insights = await wellnessAnalytics.getUserWellnessInsights(userId, parseInt(timeframe as string));
      sendSuccess(res, insights);
    } catch (error) {
      console.error('Error fetching wellness insights:', error);
      sendError(res, 'Failed to fetch wellness insights', 500);
    }
  }));

  // Get platform analytics
  app.get('/api/analytics/platform', isAuthenticated, requireAdmin, asyncHandler(async (req, res) => {
    const { timeframe = '30' } = req.query;

    try {
      const { wellnessAnalytics } = await import('./analytics/wellnessAnalytics');
      const analytics = await wellnessAnalytics.getPlatformAnalytics(parseInt(timeframe as string));
      sendSuccess(res, analytics);
    } catch (error) {
      console.error('Error fetching platform analytics:', error);
      sendError(res, 'Failed to fetch platform analytics', 500);
    }
  }));

  // Get wellness insights for specific user (admin only)
  app.get('/api/analytics/wellness/user/:userId', isAuthenticated, requireAdmin, asyncHandler(async (req, res) => {
    const { userId } = req.params;
    const { timeframe = '30' } = req.query;

    try {
      const { wellnessAnalytics } = await import('./analytics/wellnessAnalytics');
      const insights = await wellnessAnalytics.getUserWellnessInsights(userId, parseInt(timeframe as string));
      sendSuccess(res, insights);
    } catch (error) {
      console.error('Error fetching user wellness insights:', error);
      sendError(res, 'Failed to fetch user wellness insights', 500);
    }
  }));

  // Generate wellness report
  app.post('/api/analytics/wellness/report', isAuthenticated, asyncHandler(async (req, res) => {
    const userId = (req.user as any).claims.sub;
    const { reportType, timeframe = 30, includeRecommendations = true } = req.body;

    try {
      const { wellnessAnalytics } = await import('./analytics/wellnessAnalytics');
      const insights = await wellnessAnalytics.getUserWellnessInsights(userId, timeframe);

      const report = {
        generatedAt: new Date(),
        reportType,
        timeframe,
        userId,
        insights,
        summary: {
          overallRating: insights.overallScore >= 80 ? 'Excellent' : 
                        insights.overallScore >= 60 ? 'Good' : 
                        insights.overallScore >= 40 ? 'Fair' : 'Needs Improvement',
          keyStrengths: insights.trends.mood.trend === 'up' ? ['Mood improvement'] : [],
          priorityAreas: insights.achievements.improvementAreas,
          nextActions: includeRecommendations ? insights.recommendations : []
        }
      };

      sendSuccess(res, report, 'Wellness report generated successfully');
    } catch (error) {
      console.error('Error generating wellness report:', error);
      sendError(res, 'Failed to generate wellness report', 500);
    }
  }));

  const httpServer = createServer(app);
  return httpServer;
}

server/storage-backup.ts.content:
import {
  users,
  blogPosts,
  products,
  challenges,
  userChallenges,
  dailyLogs,
  wellnessPlans,
  wellnessAssessments,
  coachingSessions,
  wellnessGoals,
  fitnessData,
  type User,
  type UpsertUser,
  type BlogPost,
  type Product,
  type Challenge,
  type UserChallenge,
  type DailyLog,
  type WellnessPlan,
  type WellnessAssessment,
  type CoachingSession,
  type WellnessGoal,
  type FitnessData,
  type InsertBlogPost,
  type InsertProduct,
  type InsertChallenge,
  type InsertUserChallenge,
  type InsertDailyLog,
  type InsertWellnessPlan,
  type InsertWellnessAssessment,
  type InsertCoachingSession,
  type InsertWellnessGoal,
  type InsertFitnessData,
  wellnessQuizResponses,
  type WellnessQuizResponse,
  type InsertWellnessQuizResponse,
  aiCoachingSessions,
  type AiCoachingSession,
  type InsertAiCoachingSession,
  affiliateProducts,
  type AffiliateProduct,
  type InsertAffiliateProduct,
  affiliateLinks,
  type AffiliateLink,
  type InsertAffiliateLink,
  contentPipeline,
  type ContentPipeline,
  type InsertContentPipeline,
  socialAccounts,
  type SocialAccount,
  type InsertSocialAccount,
  automationRules,
  type AutomationRule,
  type InsertAutomationRule,
  revenueTracking,
  type RevenueTracking,
  type InsertRevenueTracking
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, lte, ilike, or, sum, avg, like, sql } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserPremiumStatus(userId: string, isPremium: boolean): Promise<void>;
  updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<void>;

  // Blog operations
  getBlogPosts(limit?: number, offset?: number, category?: string): Promise<BlogPost[]>;
  getBlogPost(slug: string): Promise<BlogPost | undefined>;
  getBlogPostById(id: number): Promise<BlogPost | undefined>;
  createBlogPost(post: InsertBlogPost): Promise<BlogPost>;
  updateBlogPost(id: number, post: Partial<InsertBlogPost>): Promise<BlogPost>;
  deleteBlogPost(id: number): Promise<void>;

  // Product operations
  getProducts(limit?: number, offset?: number, category?: string): Promise<Product[]>;
  getProduct(id: number): Promise<Product | undefined>;
  createProduct(product: InsertProduct): Promise<Product>;
  updateProduct(id: number, product: Partial<InsertProduct>): Promise<Product>;
  deleteProduct(id: number): Promise<void>;
  searchProducts(query: string): Promise<Product[]>;

  // Optimized paginated operations
  getBlogPostsPaginated(offset: number, limit: number, filters?: any): Promise<BlogPost[]>;
  getBlogPostsCount(filters?: any): Promise<number>;
  getProductsPaginated(offset: number, limit: number, filters?: any): Promise<Product[]>;
  getProductsCount(filters?: any): Promise<number>;

  // Challenge operations
  getChallenges(limit?: number, offset?: number): Promise<Challenge[]>;
  getChallenge(id: number): Promise<Challenge | undefined>;
  createChallenge(challenge: InsertChallenge): Promise<Challenge>;
  updateChallenge(id: number, challenge: Partial<InsertChallenge>): Promise<Challenge>;
  deleteChallenge(id: number): Promise<void>;

  // User challenge operations
  getUserChallenges(userId: string): Promise<UserChallenge[]>;
  getUserChallenge(userId: string, challengeId: number): Promise<UserChallenge | undefined>;
  createUserChallenge(userChallenge: InsertUserChallenge): Promise<UserChallenge>;
  updateUserChallenge(id: number, userChallenge: Partial<InsertUserChallenge>): Promise<UserChallenge>;

  // Daily log operations
  getDailyLogs(userId: string, startDate?: Date, endDate?: Date): Promise<DailyLog[]>;
  getDailyLog(userId: string, date: Date): Promise<DailyLog | undefined>;
  createDailyLog(dailyLog: InsertDailyLog): Promise<DailyLog>;
  updateDailyLog(id: number, dailyLog: Partial<InsertDailyLog>): Promise<DailyLog>;

  // Wellness coaching operations
  getWellnessPlans(userId: string): Promise<WellnessPlan[]>;
  getWellnessPlan(id: number): Promise<WellnessPlan | undefined>;
  createWellnessPlan(plan: InsertWellnessPlan): Promise<WellnessPlan>;
  updateWellnessPlan(id: number, plan: Partial<InsertWellnessPlan>): Promise<WellnessPlan>;
  deleteWellnessPlan(id: number): Promise<void>;

  // Wellness assessments
  getWellnessAssessments(userId: string, planId?: number): Promise<WellnessAssessment[]>;
  getWellnessAssessment(id: number): Promise<WellnessAssessment | undefined>;
  createWellnessAssessment(assessment: InsertWellnessAssessment): Promise<WellnessAssessment>;

  // Coaching sessions
  getCoachingSessions(userId: string, planId?: number): Promise<CoachingSession[]>;
  getCoachingSession(id: number): Promise<CoachingSession | undefined>;
  createCoachingSession(session: InsertCoachingSession): Promise<CoachingSession>;
  updateCoachingSession(id: number, session: Partial<InsertCoachingSession>): Promise<CoachingSession>;

  // Wellness goals
  getWellnessGoals(userId: string, planId?: number): Promise<WellnessGoal[]>;
  getWellnessGoal(id: number): Promise<WellnessGoal | undefined>;
  createWellnessGoal(goal: InsertWellnessGoal): Promise<WellnessGoal>;
  updateWellnessGoal(id: number, goal: Partial<InsertWellnessGoal>): Promise<WellnessGoal>;
  deleteWellnessGoal(id: number): Promise<void>;

  // Fitness data from wearable devices
  getFitnessData(userId: string, dataType?: string, startDate?: Date, endDate?: Date): Promise<FitnessData[]>;
  createFitnessData(data: InsertFitnessData): Promise<FitnessData>;
  bulkCreateFitnessData(data: InsertFitnessData[]): Promise<FitnessData[]>;
  updateUserDeviceTokens(userId: string, deviceType: string, tokens: { accessToken?: string; refreshToken?: string; userId?: string }): Promise<void>;

  // Wellness quiz operations
  createWellnessQuizResponse(response: InsertWellnessQuizResponse): Promise<WellnessQuizResponse>;
  updateUserWellnessProfile(userId: string, profile: {
    wellnessGoals: string[];
    fitnessLevel: string;
    preferredExercises: string[];
    stressLevel: number;
    sleepQuality: string;
    nutritionHabits: string;
    timeAvailability: string;
    onboardingCompleted: boolean;
  }): Promise<void>;

  // AI coaching operations
  createAiCoachingSession(session: InsertAiCoachingSession): Promise<AiCoachingSession>;
  getAiCoachingSessions(userId: string): Promise<AiCoachingSession[]>;
  updateAiCoachingSession(sessionId: number, updates: Partial<InsertAiCoachingSession>): Promise<AiCoachingSession>;

  // Affiliate products operations
  getAffiliateProducts(filters: {
    category?: string;
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<AffiliateProduct[]>;
  createAffiliateProduct(product: InsertAffiliateProduct): Promise<AffiliateProduct>;
  updateAffiliateProduct(id: number, updates: Partial<InsertAffiliateProduct>): Promise<AffiliateProduct>;

  // Automation system operations
  getAffiliateLinks(filters: {
    category?: string;
    status?: string;
    limit?: number;
    offset?: number;
  }): Promise<AffiliateLink[]>;
  createAffiliateLink(link: InsertAffiliateLink): Promise<AffiliateLink>;
  updateAffiliateLink(id: number, updates: Partial<InsertAffiliateLink>): Promise<AffiliateLink>;

  getContentPipeline(filters: {
    status?: string;
    contentType?: string;
    targetPlatform?: string;
    dueBefore?: Date;
    limit?: number;
  }): Promise<ContentPipeline[]>;
  createContentPipeline(pipeline: InsertContentPipeline): Promise<ContentPipeline>;
  updateContentPipeline(id: number, updates: Partial<InsertContentPipeline>): Promise<ContentPipeline>;

  getSocialAccounts(filters: {
    platform?: string;
    isActive?: boolean;
  }): Promise<SocialAccount[]>;
  createSocialAccount(account: InsertSocialAccount): Promise<SocialAccount>;
  updateSocialAccount(id: number, updates: Partial<InsertSocialAccount>): Promise<SocialAccount>;

  getAutomationRules(filters: {
    type?: string;
    isActive?: boolean;
    limit?: number;
  }): Promise<AutomationRule[]>;
