import { storage } from '../storage';
import { generateWellnessBlogPostDeepSeek, generateProductDescriptionDeepSeek } from '../deepseek';
import { db } from '../db';
import { products, blogPosts, affiliateLinks } from '@shared/schema';
import { eq, isNull } from 'drizzle-orm';

export interface AutomationWorkflowResult {
  productsCreated: number;
  blogsCreated: number;
  errors: string[];
  success: boolean;
}

export class ContentWorkflowAutomation {

  /**
   * Main automation workflow: Affiliate Links ‚Üí Products ‚Üí Blog Content
   */
  async runCompleteWorkflow(options: {
    processUnprocessedLinks?: boolean;
    createProducts?: boolean;
    createBlogs?: boolean;
    maxLinksToProcess?: number;
  } = {}): Promise<AutomationWorkflowResult> {

    const {
      processUnprocessedLinks = true,
      createProducts = true,
      createBlogs = true,
      maxLinksToProcess = 10
    } = options;

    const result: AutomationWorkflowResult = {
      productsCreated: 0,
      blogsCreated: 0,
      errors: [],
      success: false
    };

    try {
      console.log('üöÄ Starting Content Workflow Automation...');

      // Step 1: Get unprocessed affiliate links
      if (processUnprocessedLinks) {
        const unprocessedLinks = await this.getUnprocessedAffiliateLinks(maxLinksToProcess);
        console.log(`üìã Found ${unprocessedLinks.length} unprocessed affiliate links`);

        // Step 2: Create products from affiliate links
        if (createProducts) {
          for (const link of unprocessedLinks) {
            try {
              await this.createProductFromAffiliateLink(link);
              result.productsCreated++;
              console.log(`‚úÖ Created product: ${link.productName}`);
            } catch (error: any) {
              result.errors.push(`Failed to create product for ${link.productName}: ${error.message}`);
              console.error(`‚ùå Product creation failed:`, error);
            }
          }
        }
      }

      // Step 3: Generate blog content based on product categories
      if (createBlogs) {
        const productCategories = await this.getProductCategoriesForBlogCreation();
        console.log(`üìù Creating blogs for ${productCategories.length} categories`);

        for (const category of productCategories) {
          try {
            await this.createBlogContentForCategory(category);
            result.blogsCreated++;
            console.log(`‚úÖ Created blog for category: ${category.name}`);
          } catch (error: any) {
            result.errors.push(`Failed to create blog for category ${category.name}: ${error.message}`);
            console.error(`‚ùå Blog creation failed:`, error);
          }
        }
      }

      result.success = result.errors.length === 0;
      console.log(`üéâ Workflow completed: ${result.productsCreated} products, ${result.blogsCreated} blogs created`);

      return result;

    } catch (error: any) {
      result.errors.push(`Workflow failed: ${error.message}`);
      console.error('‚ùå Content workflow automation failed:', error);
      return result;
    }
  }

  /**
   * Get affiliate links that haven't been converted to products yet
   */
  private async getUnprocessedAffiliateLinks(limit: number = 10) {
    console.log('üîç Searching for unprocessed affiliate links...');

    const links = await db
      .select({
        id: affiliateLinks.id,
        url: affiliateLinks.url,
        merchant: affiliateLinks.merchant,
        productName: affiliateLinks.productName,
        category: affiliateLinks.category,
        commission: affiliateLinks.commission,
        description: affiliateLinks.description,
        imageUrl: affiliateLinks.imageUrl,
        status: affiliateLinks.status,
        isActive: affiliateLinks.isActive,
        createdAt: affiliateLinks.createdAt,
        updatedAt: affiliateLinks.updatedAt
      })
      .from(affiliateLinks)
      .leftJoin(products, eq(affiliateLinks.id, products.affiliateLinkId))
      .where(isNull(products.affiliateLinkId))
      .limit(limit);

    console.log(`üîç Found ${links.length} unprocessed links`);
    links.forEach(link => console.log(`   - ${link.id}: ${link.productName}`));

    return links;
  }

  /**
   * Create a product listing from affiliate link data
   */
  private async createProductFromAffiliateLink(link: any) {
    try {
      // Generate enhanced product description using AI
      const enhancedDescription = await generateProductDescriptionDeepSeek(
        link.productName,
        link.category,
        link.description || '',
        link.price || '',
        ['wellness', 'health', 'natural', 'organic']
      );

      // Extract price from string (remove currency symbols)
      const priceValue = this.extractPrice(link.price);

      // Create product record
      const [newProduct] = await db
        .insert(products)
        .values({
          name: link.productName,
          description: enhancedDescription,
          category: this.categorizeProduct(link.category),
          price: priceValue,
          imageUrl: link.imageUrl && link.imageUrl !== 'Not provided in content' ? link.imageUrl : null,
          affiliateLink: link.url,
          rating: this.estimateRating(link.commission, link.merchant),
          isRecommended: link.commission > 5, // Mark high-commission items as recommended
          affiliateLinkId: link.id,
          autoGenerated: true
        })
        .returning();

      console.log(`‚úÖ Created product: ${newProduct.name} in category: ${newProduct.category}`);
      return newProduct;

    } catch (error: any) {
      console.error(`‚ùå Failed to create product from affiliate link:`, error);
      throw error;
    }
  }

  /**
   * Get product categories that need blog content
   */
  private async getProductCategoriesForBlogCreation() {
    const categories = await db
      .selectDistinct({ 
        name: products.category,
        count: products.id
      })
      .from(products)
      .where(eq(products.autoGenerated, true));

    return categories.map(cat => ({
      name: cat.name,
      count: 1 // Simplified for now
    })).filter(cat => cat.name);
  }

  /**
   * Create blog content focused on a product category
   */
  private async createBlogContentForCategory(category: { name: string; count: number }) {
    try {
      // Get products in this category
      const categoryProducts = await db
        .select()
        .from(products)
        .where(eq(products.category, category.name))
        .limit(5);

      if (categoryProducts.length === 0) return;

      // Generate blog title and content
      const blogTitle = this.generateBlogTitle(category.name, categoryProducts);
      const slug = this.generateSlug(blogTitle);

      // Check if blog already exists for this category
      const existingBlog = await db
        .select()
        .from(blogPosts)
        .where(eq(blogPosts.slug, slug))
        .limit(1);

      if (existingBlog.length > 0) {
        console.log(`üìù Blog already exists for category: ${category.name}`);
        return;
      }

      // Generate comprehensive blog content
      const productNames = categoryProducts.map(p => p.name).join(', ');
      const categoryKeywords = this.getCategoryKeywords(category.name);

      const blogContent = await generateWellnessBlogPostDeepSeek(
        blogTitle,
        category.name,
        [
          `Product recommendations including: ${productNames}`,
          `Wellness benefits of ${categoryKeywords}`,
          `How to choose the right ${category.name.toLowerCase()} products`,
          `Expert tips and recommendations`
        ].join('\n'),
        categoryKeywords
      );

      // Create blog post
      const [newBlog] = await db
        .insert(blogPosts)
        .values({
          title: blogTitle,
          slug,
          excerpt: `Discover the best ${category.name.toLowerCase()} products and wellness tips from our expert recommendations.`,
          content: blogContent,
          category: category.name,
          tags: [category.name.toLowerCase(), 'product-review', 'wellness', 'recommendations'],
          isPremium: false,
          isPublished: true,
          readTime: Math.ceil(blogContent.length / 1000), // Estimate reading time
          authorId: 'system' // System-generated content
        })
        .returning();

      console.log(`‚úÖ Created blog: ${newBlog.title}`);
      return newBlog;

    } catch (error: any) {
      console.error(`‚ùå Failed to create blog for category:`, error);
      throw error;
    }
  }

  /**
   * Utility functions
   */
  private extractPrice(priceString: string): string {
    if (!priceString) return '0';

    // Extract numeric value from price string (e.g., "¬£7.99" -> "7.99")
    const match = priceString.match(/[\d.]+/);
    return match ? match[0] : '0';
  }

  private categorizeProduct(originalCategory: string): string {
    if (!originalCategory) return 'general';

    const categoryMap: Record<string, string> = {
      'supplements': 'nutrition',
      'vitamins': 'nutrition', 
      'protein': 'nutrition',
      'omega': 'nutrition',
      'fitness': 'fitness',
      'exercise': 'fitness',
      'meditation': 'mindfulness',
      'mindfulness': 'mindfulness',
      'sleep': 'wellness',
      'skincare': 'beauty',
      'essential oils': 'aromatherapy',
      'chia': 'nutrition',
      'superfoods': 'nutrition'
    };

    const lower = originalCategory.toLowerCase();
    for (const [key, value] of Object.entries(categoryMap)) {
      if (lower.includes(key)) {
        return value;
      }
    }

    return 'wellness';
  }

  private estimateRating(commission: number, merchant: string): string {
    // Higher commission and known merchants get better ratings
    let rating = 4.0;

    if (commission > 7) rating += 0.5;
    if (commission > 10) rating += 0.3;
    if (merchant.toLowerCase().includes('amazon')) rating += 0.2;

    return Math.min(rating, 5.0).toFixed(1);
  }

  private generateBlogTitle(category: string, products: any[]): string {
    const titles = [
      `The Ultimate Guide to ${category} Products for Wellness`,
      `Top ${category} Recommendations for a Healthier Lifestyle`,
      `Best ${category} Products: Expert Reviews and Recommendations`,
      `Transform Your Wellness Journey with These ${category} Essentials`,
      `${category} Products That Will Revolutionize Your Health Routine`
    ];

    return titles[Math.floor(Math.random() * titles.length)];
  }

  private generateSlug(title: string): string {
    return title
      .toLowerCase()
      .replace(/[^\w\s-]/g, '')
      .replace(/[\s_-]+/g, '-')
      .replace(/^-+|-+$/g, '');
  }

  private getCategoryKeywords(category: string): string[] {
    const keywordMap: Record<string, string[]> = {
      nutrition: ['supplements', 'vitamins', 'healthy eating', 'nutrients', 'wellness'],
      fitness: ['exercise', 'workout', 'training', 'fitness equipment', 'health'],
      mindfulness: ['meditation', 'mindfulness', 'mental health', 'stress relief', 'peace'],
      wellness: ['health', 'wellness', 'lifestyle', 'natural', 'holistic'],
      beauty: ['skincare', 'natural beauty', 'self-care', 'radiant skin'],
      aromatherapy: ['essential oils', 'aromatherapy', 'natural scents', 'relaxation']
    };

    return keywordMap[category.toLowerCase()] || ['wellness', 'health', 'lifestyle'];
  }
}

// Export singleton instance
export const contentWorkflow = new ContentWorkflowAutomation();

server/automation/intelligentScheduler.t.content:
import { db } from '../db';
import { automationSchedule, blogPosts, products } from '@shared/schema';
import { eq, gte, desc, and, count } from 'drizzle-orm';

export interface ScheduleTask {
  id?: number;
  type: 'CONTENT_CREATION' | 'AFFILIATE_SCRAPING' | 'BLOG_OPTIMIZATION' | 'PRODUCT_UPDATE' | 'ANALYTICS_REPORT';
  priority: 'LOW' | 'MEDIUM' | 'HIGH' | 'URGENT';
  scheduledFor: Date;
  parameters: any;
  status: 'PENDING' | 'IN_PROGRESS' | 'COMPLETED' | 'FAILED';
  retryCount: number;
  maxRetries: number;
  estimatedDuration: number; // minutes
}

export class IntelligentScheduler {
  private isRunning: boolean = false;
  private taskQueue: ScheduleTask[] = [];

  async startScheduler(): Promise<void> {
    if (this.isRunning) return;

    this.isRunning = true;
    console.log('üìÖ Intelligent Scheduler started');

    // Load pending tasks from database
    await this.loadPendingTasks();

    // Start task processing loop
    this.processTaskQueue();
  }

  async stopScheduler(): Promise<void> {
    this.isRunning = false;
    console.log('üìÖ Intelligent Scheduler stopped');
  }

  private async loadPendingTasks(): Promise<void> {
    try {
      const pendingTasks = await db
        .select()
        .from(automationSchedule)
        .where(eq(automationSchedule.status, 'PENDING'))
        .orderBy(desc(automationSchedule.priority), automationSchedule.scheduledFor);

      this.taskQueue = pendingTasks.map(task => ({
        id: task.id,
        type: task.type as any,
        priority: task.priority as any,
        scheduledFor: task.scheduledFor,
        parameters: JSON.parse(task.parameters || '{}'),
        status: task.status as any,
        retryCount: task.retryCount || 0,
        maxRetries: task.maxRetries || 3,
        estimatedDuration: task.estimatedDuration || 15
      }));

      console.log(`üìã Loaded ${this.taskQueue.length} pending tasks`);
    } catch (error) {
      console.error('Failed to load pending tasks:', error);
    }
  }

  async scheduleTask(task: Omit<ScheduleTask, 'id' | 'status' | 'retryCount'>): Promise<number> {
    try {
      // Use intelligent scheduling to optimize timing
      const optimizedTime = await this.optimizeSchedulingTime(task);

      const [insertedTask] = await db
        .insert(automationSchedule)
        .values({
          type: task.type,
          priority: task.priority,
          scheduledFor: optimizedTime,
          parameters: JSON.stringify(task.parameters || {}),
          status: 'PENDING',
          retryCount: 0,
          maxRetries: task.maxRetries,
          estimatedDuration: task.estimatedDuration
        })
        .returning();

      // Add to in-memory queue
      this.taskQueue.push({
        ...task,
        id: insertedTask.id,
        status: 'PENDING',
        retryCount: 0,
        scheduledFor: optimizedTime
      });

      // Sort queue by priority and time
      this.sortTaskQueue();

      console.log(`üìù Scheduled ${task.type} task for ${optimizedTime.toISOString()}`);
      return insertedTask.id;
    } catch (error) {
      console.error('Failed to schedule task:', error);
      throw error;
    }
  }

  private async optimizeSchedulingTime(task: ScheduleTask): Promise<Date> {
    const now = new Date();
    const requestedTime = task.scheduledFor;

    // Don't schedule in the past
    if (requestedTime <= now) {
      return new Date(now.getTime() + 5 * 60 * 1000); // 5 minutes from now
    }

    // For high-priority tasks, schedule immediately if system is available
    if (task.priority === 'URGENT' || task.priority === 'HIGH') {
      const systemLoad = await this.getSystemLoad();
      if (systemLoad < 70) {
        return new Date(now.getTime() + 2 * 60 * 1000); // 2 minutes from now
      }
    }

    // Optimize based on historical performance
    const optimalHour = await this.getOptimalExecutionHour(task.type);

    // If requesting time is during optimal hours, keep it
    if (requestedTime.getHours() >= optimalHour && requestedTime.getHours() <= optimalHour + 2) {
      return requestedTime;
    }

    // Otherwise, suggest optimal time
    const optimizedDate = new Date(requestedTime);
    optimizedDate.setHours(optimalHour, 0, 0, 0);

    // If optimal time is in the past today, schedule for tomorrow
    if (optimizedDate <= now) {
      optimizedDate.setDate(optimizedDate.getDate() + 1);
    }

    return optimizedDate;
  }

  private async getOptimalExecutionHour(taskType: string): Promise<number> {
    // Analyze historical task performance to determine optimal execution times
    const hourlyPerformance = new Map<number, { success: number; total: number }>();

    // Initialize with default optimal hours based on task type
    const defaultOptimalHours = {
      'CONTENT_CREATION': 9,  // 9 AM - good for creative work
      'AFFILIATE_SCRAPING': 3, // 3 AM - less traffic
      'BLOG_OPTIMIZATION': 11, // 11 AM - mid-morning
      'PRODUCT_UPDATE': 2,     // 2 AM - minimal interference
      'ANALYTICS_REPORT': 6    // 6 AM - before business hours
    };

    return defaultOptimalHours[taskType] || 9;
  }

  private async getSystemLoad(): Promise<number> {
    // Calculate system load based on active tasks and system metrics
    const activeTasks = this.taskQueue.filter(task => task.status === 'IN_PROGRESS').length;
    const pendingTasks = this.taskQueue.filter(task => task.status === 'PENDING').length;

    // Simple load calculation (0-100%)
    const load = Math.min((activeTasks * 20) + (pendingTasks * 5), 100);
    return load;
  }

  private async processTaskQueue(): Promise<void> {
    if (!this.isRunning) return;

    try {
      const now = new Date();
      const readyTasks = this.taskQueue.filter(
        task => task.status === 'PENDING' && task.scheduledFor <= now
      );

      for (const task of readyTasks.slice(0, 3)) { // Process max 3 tasks concurrently
        await this.executeTask(task);
      }

      // Schedule next check
      setTimeout(() => this.processTaskQueue(), 30000); // Check every 30 seconds
    } catch (error) {
      console.error('Error processing task queue:', error);
      setTimeout(() => this.processTaskQueue(), 60000); // Retry in 1 minute
    }
  }

  private async executeTask(task: ScheduleTask): Promise<void> {
    try {
      console.log(`üîÑ Executing task: ${task.type} (ID: ${task.id})`);

      // Update task status
      task.status = 'IN_PROGRESS';
      await this.updateTaskStatus(task.id!, 'IN_PROGRESS');

      let success = false;

      switch (task.type) {
        case 'CONTENT_CREATION':
          success = await this.executeContentCreation(task.parameters);
          break;
        case 'AFFILIATE_SCRAPING':
          success = await this.executeAffiliateScraping(task.parameters);
          break;
        case 'BLOG_OPTIMIZATION':
          success = await this.executeBlogOptimization(task.parameters);
          break;
        case 'PRODUCT_UPDATE':
          success = await this.executeProductUpdate(task.parameters);
          break;
        case 'ANALYTICS_REPORT':
          success = await this.executeAnalyticsReport(task.parameters);
          break;
        default:
          console.warn(`Unknown task type: ${task.type}`);
          success = false;
      }

      if (success) {
        task.status = 'COMPLETED';
        await this.updateTaskStatus(task.id!, 'COMPLETED');
        console.log(`‚úÖ Task completed: ${task.type}`);
      } else {
        throw new Error(`Task execution failed: ${task.type}`);
      }

    } catch (error) {
      console.error(`‚ùå Task failed: ${task.type}`, error);

      task.retryCount++;
      if (task.retryCount < task.maxRetries) {
        // Reschedule for retry
        task.status = 'PENDING';
        task.scheduledFor = new Date(Date.now() + (task.retryCount * 10 * 60 * 1000)); // Exponential backoff
        await this.updateTaskStatus(task.id!, 'PENDING');
        console.log(`üîÑ Rescheduling task ${task.type} for retry ${task.retryCount}`);
      } else {
        task.status = 'FAILED';
        await this.updateTaskStatus(task.id!, 'FAILED');
        console.log(`üíÄ Task permanently failed: ${task.type}`);
      }
    }
  }

  private async executeContentCreation(parameters: any): Promise<boolean> {
    try {
      const { contentWorkflow } = await import('./contentWorkflow');
      const result = await contentWorkflow.runCompleteWorkflow({
        processUnprocessedLinks: true,
        createProducts: true,
        createBlogs: true,
        maxLinksToProcess: parameters.maxLinks || 3,
        ...parameters
      });
      return result.success;
    } catch (error) {
      console.error('Content creation failed:', error);
      return false;
    }
  }

  private async executeAffiliateScraping(parameters: any): Promise<boolean> {
    try {
      // This would implement intelligent affiliate link discovery
      // For now, simulate success
      await new Promise(resolve => setTimeout(resolve, 2000));
      return true;
    } catch (error) {
      console.error('Affiliate scraping failed:', error);
      return false;
    }
  }

  private async executeBlogOptimization(parameters: any): Promise<boolean> {
    try {
      // Get blogs that need optimization
      const blogs = await db
        .select()
        .from(blogPosts)
        .where(eq(blogPosts.isPublished, true))
        .limit(parameters.maxBlogs || 5);

      for (const blog of blogs) {
        // Simulate optimization
        await db
          .update(blogPosts)
          .set({
            title: `${blog.title} - Updated`,
            updatedAt: new Date()
          })
          .where(eq(blogPosts.id, blog.id));
      }

      return true;
    } catch (error) {
      console.error('Blog optimization failed:', error);
      return false;
    }
  }

  private async executeProductUpdate(parameters: any): Promise<boolean> {
    try {
      // Update product information
      const [result] = await db
        .select({ count: count() })
        .from(products);

      console.log(`Updated ${result?.count || 0} products`);
      return true;
    } catch (error) {
      console.error('Product update failed:', error);
      return false;
    }
  }

  private async executeAnalyticsReport(parameters: any): Promise<boolean> {
    try {
      // Generate analytics report
      console.log('Analytics report generated');
      return true;
    } catch (error) {
      console.error('Analytics report failed:', error);
      return false;
    }
  }

  private async updateTaskStatus(taskId: number, status: string): Promise<void> {
    try {
      await db
        .update(automationSchedule)
        .set({ 
          status,
          updatedAt: new Date()
        })
        .where(eq(automationSchedule.id, taskId));
    } catch (error) {
      console.error('Failed to update task status:', error);
    }
  }

  private sortTaskQueue(): void {
    const priorityOrder = { 'URGENT': 4, 'HIGH': 3, 'MEDIUM': 2, 'LOW': 1 };

    this.taskQueue.sort((a, b) => {
      const priorityDiff = priorityOrder[b.priority] - priorityOrder[a.priority];
      if (priorityDiff !== 0) return priorityDiff;

      return a.scheduledFor.getTime() - b.scheduledFor.getTime();
    });
  }

  // Public methods
  async getScheduledTasks(): Promise<ScheduleTask[]> {
    return this.taskQueue.filter(task => task.status === 'PENDING');
  }

  async cancelTask(taskId: number): Promise<boolean> {
    try {
      await db
        .update(automationSchedule)
        .set({ status: 'CANCELLED' })
        .where(eq(automationSchedule.id, taskId));

      this.taskQueue = this.taskQueue.filter(task => task.id !== taskId);
      return true;
    } catch (error) {
      console.error('Failed to cancel task:', error);
      return false;
    }
  }

  getSchedulerStatus() {
    return {
      isRunning: this.isRunning,
      queueLength: this.taskQueue.length,
      activeTasks: this.taskQueue.filter(t => t.status === 'IN_PROGRESS').length,
      pendingTasks: this.taskQueue.filter(t => t.status === 'PENDING').length,
      completedTasks: this.taskQueue.filter(t => t.status === 'COMPLETED').length,
      failedTasks: this.taskQueue.filter(t => t.status === 'FAILED').length
    };
  }
}

// Export singleton instance
export const intelligentScheduler = new IntelligentScheduler();

