  const revenueMetrics = [
    { label: "Monthly Revenue", value: "$3,450", change: "+23%", icon: DollarSign, color: "text-green-600" },
    { label: "Conversion Rate", value: "7.1%", change: "+1.2%", icon: Percent, color: "text-blue-600" },
