import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { 
  Watch, 
  Smartphone, 
  Activity, 
  Heart, 
  Moon, 
  TrendingUp,
  CheckCircle,
  XCircle,
  RefreshCw,
  Settings,
  Calendar,
  BarChart3
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface FitnessData {
  id: number;
  deviceType: string;
  dataType: string;
  value: string;
  unit: string;
  recordedAt: string;
  metadata?: {
    deviceModel?: string;
    workoutType?: string;
  };
}

interface DeviceConnection {
  fitbitConnected: boolean;
  appleHealthConnected: boolean;
  lastSyncAt?: string;
}

export default function DeviceIntegration() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch device connection status
  const { data: deviceStatus, isLoading: statusLoading } = useQuery<DeviceConnection>({
    queryKey: ['/api/devices/status'],
  });

  // Fetch recent fitness data
  const { data: fitnessData, isLoading: dataLoading } = useQuery<FitnessData[]>({
    queryKey: ['/api/fitness/data'],
  });

  // Connect to Fitbit
  const connectFitbit = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('GET', '/api/devices/fitbit/auth');
      const data = await response.json();
      window.location.href = data.authUrl;
    },
    onError: (error) => {
      toast({
        title: "Connection Failed",
        description: "Unable to connect to Fitbit. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Sync data manually
  const syncData = useMutation({
    mutationFn: async () => {
      await apiRequest('POST', '/api/devices/sync');
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/fitness/data'] });
      queryClient.invalidateQueries({ queryKey: ['/api/devices/status'] });
      toast({
        title: "Sync Complete",
        description: "Your fitness data has been updated successfully.",
      });
    },
    onError: (error) => {
      toast({
        title: "Sync Failed",
        description: "Unable to sync your fitness data. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Disconnect device
  const disconnectDevice = useMutation({
    mutationFn: async (deviceType: string) => {
      await apiRequest('POST', '/api/devices/disconnect', { deviceType });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/devices/status'] });
      toast({
        title: "Device Disconnected",
        description: "Your device has been disconnected successfully.",
      });
    },
    onError: (error) => {
      toast({
        title: "Disconnect Failed",
        description: "Unable to disconnect device. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Helper function to format date
  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // Get data type icon
  const getDataTypeIcon = (dataType: string) => {
    switch (dataType) {
      case 'steps': return <Activity className="w-4 h-4" />;
      case 'heart_rate': return <Heart className="w-4 h-4" />;
      case 'sleep': return <Moon className="w-4 h-4" />;
      case 'calories': return <TrendingUp className="w-4 h-4" />;
      default: return <BarChart3 className="w-4 h-4" />;
    }
  };

  // Get device type color
  const getDeviceColor = (deviceType: string) => {
    switch (deviceType) {
      case 'fitbit': return 'bg-blue-100 text-blue-700 border-blue-200';
      case 'apple_health': return 'bg-gray-100 text-gray-700 border-gray-200';
      default: return 'bg-tulsi-100 text-tulsi-700 border-tulsi-200';
    }
  };

  if (statusLoading || dataLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-tulsi-50 to-white py-8">
        <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-tulsi-200 rounded w-1/3"></div>
            <div className="grid md:grid-cols-2 gap-6">
              <div className="h-64 bg-tulsi-100 rounded-lg"></div>
              <div className="h-64 bg-tulsi-100 rounded-lg"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-tulsi-50 to-white py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mb-8"
        >
          <h1 className="text-3xl font-bold text-tulsi-800 mb-2">Device Integration</h1>
          <p className="text-tulsi-600 font-light">
            Connect your wearable devices to track your wellness journey automatically
          </p>
        </motion.div>

        {/* Device Status Cards */}
        <div className="grid md:grid-cols-2 gap-6 mb-8">
          {/* Fitbit Card */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 }}
          >
            <Card className="border-tulsi-200">
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                      <Watch className="w-6 h-6 text-blue-600" />
                    </div>
                    <div>
                      <CardTitle className="text-lg">Fitbit</CardTitle>
                      <CardDescription>Sync fitness and health data</CardDescription>
                    </div>
                  </div>
                  {deviceStatus?.fitbitConnected ? (
                    <CheckCircle className="w-6 h-6 text-green-500" />
                  ) : (
                    <XCircle className="w-6 h-6 text-gray-400" />
                  )}
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-600">Status</span>
                    <Badge className={deviceStatus?.fitbitConnected ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-700'}>
                      {deviceStatus?.fitbitConnected ? 'Connected' : 'Not Connected'}
                    </Badge>
                  </div>

                  {deviceStatus?.fitbitConnected && deviceStatus.lastSyncAt && (
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-gray-600">Last Sync</span>
                      <span className="text-sm text-tulsi-600">{formatDate(deviceStatus.lastSyncAt)}</span>
                    </div>
                  )}

                  <div className="flex space-x-2">
                    {deviceStatus?.fitbitConnected ? (
                      <>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => syncData.mutate()}
                          disabled={syncData.isPending}
                          className="flex-1"
                        >
                          <RefreshCw className={`w-4 h-4 mr-2 ${syncData.isPending ? 'animate-spin' : ''}`} />
                          Sync Now
                        </Button>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => disconnectDevice.mutate('fitbit')}
                          disabled={disconnectDevice.isPending}
                        >
                          <Settings className="w-4 h-4" />
                        </Button>
                      </>
                    ) : (
                      <Button
                        size="sm"
                        className="w-full bg-blue-600 hover:bg-blue-700"
                        onClick={() => connectFitbit.mutate()}
                        disabled={connectFitbit.isPending}
                      >
                        Connect Fitbit
                      </Button>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          </motion.div>

          {/* Apple Health Card */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
          >
            <Card className="border-tulsi-200">
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-12 h-12 bg-gray-100 rounded-lg flex items-center justify-center">
                      <Smartphone className="w-6 h-6 text-gray-600" />
                    </div>
                    <div>
                      <CardTitle className="text-lg">Apple Health</CardTitle>
                      <CardDescription>iPhone health data integration</CardDescription>
                    </div>
                  </div>
                  {deviceStatus?.appleHealthConnected ? (
                    <CheckCircle className="w-6 h-6 text-green-500" />
                  ) : (
                    <XCircle className="w-6 h-6 text-gray-400" />
                  )}
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-600">Status</span>
                    <Badge className="bg-orange-100 text-orange-700">
                      Coming Soon
                    </Badge>
                  </div>

                  <p className="text-sm text-gray-500">
                    Apple Health integration will be available soon. Connect via the iOS app when released.
                  </p>

                  <Button
                    size="sm"
                    variant="outline"
                    disabled
                    className="w-full"
                  >
                    Connect Apple Health
                  </Button>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        </div>

        {/* Recent Fitness Data */}
        {fitnessData && fitnessData.length > 0 && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <Card className="border-tulsi-200">
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle className="text-xl">Recent Activity Data</CardTitle>
                    <CardDescription>Your latest fitness metrics from connected devices</CardDescription>
                  </div>
                  <Calendar className="w-5 h-5 text-tulsi-600" />
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {fitnessData.slice(0, 10).map((data) => (
                    <div key={data.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center space-x-3">
                        {getDataTypeIcon(data.dataType)}
                        <div>
                          <p className="font-medium text-gray-900 capitalize">
                            {data.dataType.replace('_', ' ')}: {data.value} {data.unit}
                          </p>
                          <p className="text-sm text-gray-500">{formatDate(data.recordedAt)}</p>
                        </div>
                      </div>
                      <Badge className={getDeviceColor(data.deviceType)}>
                        {data.deviceType}
                      </Badge>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </motion.div>
        )}

        {/* Empty State */}
        {(!fitnessData || fitnessData.length === 0) && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <Card className="border-tulsi-200">
              <CardContent className="py-12 text-center">
                <BarChart3 className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No Fitness Data Yet</h3>
                <p className="text-gray-500 mb-6">
                  Connect your devices and sync data to see your wellness metrics here.
                </p>
                {deviceStatus?.fitbitConnected && (
                  <Button onClick={() => syncData.mutate()} disabled={syncData.isPending}>
                    <RefreshCw className={`w-4 h-4 mr-2 ${syncData.isPending ? 'animate-spin' : ''}`} />
                    Sync Data Now
                  </Button>
                )}
              </CardContent>
            </Card>
          </motion.div>
        )}
      </div>
    </div>
  );
}

client/src/pages/meal-planner.tsx.content:
import { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { useSubscription } from "@/hooks/useSubscription";
import { useAuth } from "@/hooks/useAuth";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useMutation } from "@tanstack/react-query";
import { 
  Crown, 
  UtensilsCrossed, 
  Clock, 
  Users, 
  ChefHat,
  Sparkles,
  Heart,
  Leaf,
  Target,
  Calendar,
  CheckCircle,
  Info
} from "lucide-react";

interface MealPlan {
  id: string;
  title: string;
  description: string;
  meals: {
    breakfast: MealItem[];
    lunch: MealItem[];
    dinner: MealItem[];
    snacks: MealItem[];
  };
  nutritionSummary: {
    totalCalories: number;
    protein: number;
    carbs: number;
    fat: number;
    fiber: number;
  };
  shoppingList: string[];
  preparationTips: string[];
  createdAt: Date;
}

interface MealItem {
  name: string;
  description: string;
  calories: number;
  prepTime: number;
  ingredients: string[];
  instructions: string[];
  nutritionInfo: {
    protein: number;
    carbs: number;
    fat: number;
    fiber: number;
  };
}

interface MealPlanRequest {
  dietaryPreferences: string[];
  healthGoals: string[];
  allergies: string[];
  calorieTarget: number;
  mealsPerDay: number;
  cookingTime: string;
  servingSize: number;
  additionalNotes: string;
}

export default function MealPlanner() {
  const { t } = useTranslation();
  const { subscription, isPremium, isLoading: subscriptionLoading } = useSubscription();
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const [currentMealPlan, setCurrentMealPlan] = useState<MealPlan | null>(null);
  const [formData, setFormData] = useState<MealPlanRequest>({
    dietaryPreferences: [],
    healthGoals: [],
    allergies: [],
    calorieTarget: 2000,
    mealsPerDay: 3,
    cookingTime: "30-45",
    servingSize: 1,
    additionalNotes: ""
  });

  const generateMealPlan = useMutation({
    mutationFn: async (data: MealPlanRequest) => {
      const response = await apiRequest("POST", "/api/meal-planner/generate", data);
      return response.json();
    },
    onSuccess: (data) => {
      setCurrentMealPlan(data.mealPlan);
      toast({
        title: "Meal Plan Generated!",
        description: "Your personalized meal plan is ready.",
      });
    },
    onError: (error) => {
      toast({
        title: "Generation Failed",
        description: "Failed to generate meal plan. Please try again.",
        variant: "destructive",
      });
    },
  });

  const dietaryOptions = [
    { id: "vegetarian", label: "Vegetarian", icon: <Leaf className="w-4 h-4" /> },
    { id: "vegan", label: "Vegan", icon: <Leaf className="w-4 h-4" /> },
    { id: "keto", label: "Ketogenic", icon: <Target className="w-4 h-4" /> },
    { id: "paleo", label: "Paleo", icon: <Target className="w-4 h-4" /> },
    { id: "mediterranean", label: "Mediterranean", icon: <Heart className="w-4 h-4" /> },
    { id: "lowcarb", label: "Low Carb", icon: <Target className="w-4 h-4" /> },
    { id: "glutenfree", label: "Gluten-Free", icon: <Leaf className="w-4 h-4" /> },
    { id: "dairyfree", label: "Dairy-Free", icon: <Leaf className="w-4 h-4" /> }
  ];

  const healthGoalOptions = [
    { id: "weight-loss", label: "Weight Loss" },
    { id: "weight-gain", label: "Weight Gain" },
    { id: "muscle-building", label: "Muscle Building" },
    { id: "energy-boost", label: "Energy Boost" },
    { id: "heart-health", label: "Heart Health" },
    { id: "digestive-health", label: "Digestive Health" },
    { id: "immune-support", label: "Immune Support" },
    { id: "maintenance", label: "Maintenance" }
  ];

  const handleDietaryChange = (optionId: string, checked: boolean) => {
    setFormData(prev => ({
      ...prev,
      dietaryPreferences: checked 
        ? [...prev.dietaryPreferences, optionId]
        : prev.dietaryPreferences.filter(id => id !== optionId)
    }));
  };

  const handleHealthGoalChange = (optionId: string, checked: boolean) => {
    setFormData(prev => ({
      ...prev,
      healthGoals: checked 
        ? [...prev.healthGoals, optionId]
        : prev.healthGoals.filter(id => id !== optionId)
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!isPremium) {
      toast({
        title: "Premium Feature",
        description: "AI Meal Planner is available for premium subscribers only.",
        variant: "destructive",
      });
      return;
    }
    generateMealPlan.mutate(formData);
  };

  if (subscriptionLoading || !isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-primary/5 to-primary/10 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4" />
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary/5 to-primary/10 py-8">
      <div className="max-w-6xl mx-auto px-4">
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-2 mb-4">
            <UtensilsCrossed className="w-8 h-8 text-primary" />
            <Badge variant="secondary" className="gap-1">
              <Crown className="w-4 h-4" />
              Premium Feature
            </Badge>
          </div>
          <h1 className="text-3xl font-bold text-foreground mb-2">
            AI Meal Planner
          </h1>
          <p className="text-muted-foreground max-w-2xl mx-auto">
            Get personalized meal plans generated by AI based on your dietary preferences, health goals, and lifestyle
          </p>
        </div>

        {!isPremium ? (
          <Card className="max-w-2xl mx-auto">
            <CardHeader className="text-center">
              <Crown className="w-12 h-12 text-primary mx-auto mb-4" />
              <CardTitle>Premium Feature Required</CardTitle>
              <CardDescription>
                The AI Meal Planner is available for premium subscribers only. 
                Upgrade to access personalized meal planning with AI-generated recipes.
              </CardDescription>
            </CardHeader>
            <CardContent className="text-center">
              <Button 
                onClick={() => window.location.href = '/subscribe'}
                className="bg-gradient-to-r from-primary to-primary/80 hover:from-primary/90 hover:to-primary/70"
              >
                <Crown className="w-4 h-4 mr-2" />
                Upgrade to Premium
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="grid lg:grid-cols-2 gap-8">
            {/* Configuration Form */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <ChefHat className="w-5 h-5" />
                  Meal Plan Configuration
                </CardTitle>
                <CardDescription>
                  Tell us about your preferences and we'll create a personalized meal plan
                </CardDescription>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleSubmit} className="space-y-6">
                  {/* Dietary Preferences */}
                  <div>
                    <Label className="text-sm font-medium mb-3 block">Dietary Preferences</Label>
                    <div className="grid grid-cols-2 gap-3">
                      {dietaryOptions.map((option) => (
                        <div key={option.id} className="flex items-center space-x-2">
                          <input
                            type="checkbox"
                            id={option.id}
                            checked={formData.dietaryPreferences.includes(option.id)}
                            onChange={(e) => handleDietaryChange(option.id, e.target.checked)}
                            className="rounded border-gray-300"
                          />
                          <Label htmlFor={option.id} className="text-sm flex items-center gap-2 cursor-pointer">
                            {option.icon}
                            {option.label}
                          </Label>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Health Goals */}
                  <div>
                    <Label className="text-sm font-medium mb-3 block">Health Goals</Label>
                    <div className="grid grid-cols-2 gap-3">
                      {healthGoalOptions.map((goal) => (
                        <div key={goal.id} className="flex items-center space-x-2">
                          <input
                            type="checkbox"
                            id={goal.id}
                            checked={formData.healthGoals.includes(goal.id)}
                            onChange={(e) => handleHealthGoalChange(goal.id, e.target.checked)}
                            className="rounded border-gray-300"
                          />
                          <Label htmlFor={goal.id} className="text-sm cursor-pointer">
                            {goal.label}
                          </Label>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Calorie Target */}
                  <div>
                    <Label htmlFor="calories" className="text-sm font-medium">Daily Calorie Target</Label>
                    <Input
                      id="calories"
                      type="number"
                      value={formData.calorieTarget}
                      onChange={(e) => setFormData(prev => ({ ...prev, calorieTarget: parseInt(e.target.value) }))}
                      min="1200"
                      max="4000"
                      className="mt-2"
                    />
                  </div>

                  {/* Cooking Time */}
                  <div>
                    <Label className="text-sm font-medium mb-2 block">Preferred Cooking Time</Label>
                    <RadioGroup
                      value={formData.cookingTime}
                      onValueChange={(value) => setFormData(prev => ({ ...prev, cookingTime: value }))}
                    >
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="15-30" id="time1" />
                        <Label htmlFor="time1" className="text-sm">15-30 minutes</Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="30-45" id="time2" />
                        <Label htmlFor="time2" className="text-sm">30-45 minutes</Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="45-60" id="time3" />
                        <Label htmlFor="time3" className="text-sm">45-60 minutes</Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="60+" id="time4" />
                        <Label htmlFor="time4" className="text-sm">60+ minutes</Label>
                      </div>
                    </RadioGroup>
                  </div>

                  {/* Serving Size */}
                  <div>
                    <Label htmlFor="servings" className="text-sm font-medium">Serving Size</Label>
                    <Select value={formData.servingSize.toString()} onValueChange={(value) => setFormData(prev => ({ ...prev, servingSize: parseInt(value) }))}>
                      <SelectTrigger className="mt-2">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="1">1 person</SelectItem>
                        <SelectItem value="2">2 people</SelectItem>
                        <SelectItem value="3">3 people</SelectItem>
                        <SelectItem value="4">4 people</SelectItem>
                        <SelectItem value="5">5+ people</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  {/* Additional Notes */}
                  <div>
                    <Label htmlFor="notes" className="text-sm font-medium">Additional Notes</Label>
                    <Textarea
                      id="notes"
                      placeholder="Any allergies, specific preferences, or additional requirements..."
                      value={formData.additionalNotes}
                      onChange={(e) => setFormData(prev => ({ ...prev, additionalNotes: e.target.value }))}
                      className="mt-2"
                    />
                  </div>

                  <Button 
                    type="submit" 
                    disabled={generateMealPlan.isPending}
                    className="w-full bg-gradient-to-r from-primary to-primary/80 hover:from-primary/90 hover:to-primary/70"
                  >
                    {generateMealPlan.isPending ? (
                      <div className="flex items-center gap-2">
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                        Generating Meal Plan...
                      </div>
                    ) : (
                      <div className="flex items-center gap-2">
                        <Sparkles className="w-4 h-4" />
                        Generate AI Meal Plan
                      </div>
                    )}
                  </Button>
                </form>
              </CardContent>
            </Card>

            {/* Generated Meal Plan */}
            {currentMealPlan && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Calendar className="w-5 h-5" />
                    {currentMealPlan.title}
                  </CardTitle>
                  <CardDescription>
                    {currentMealPlan.description}
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                  {/* Nutrition Summary */}
                  <div className="grid grid-cols-2 gap-4">
                    <div className="bg-primary/10 p-4 rounded-lg">
                      <div className="text-2xl font-bold text-primary">
                        {currentMealPlan.nutritionSummary.totalCalories}
                      </div>
                      <div className="text-sm text-muted-foreground">Total Calories</div>
                    </div>
                    <div className="bg-primary/10 p-4 rounded-lg">
                      <div className="text-lg font-semibold">
                        P: {currentMealPlan.nutritionSummary.protein}g
                      </div>
                      <div className="text-lg font-semibold">
                        C: {currentMealPlan.nutritionSummary.carbs}g
                      </div>
                      <div className="text-lg font-semibold">
                        F: {currentMealPlan.nutritionSummary.fat}g
                      </div>
                    </div>
                  </div>

                  {/* Daily Meals */}
                  <div className="space-y-4">
                    {Object.entries(currentMealPlan.meals).map(([mealType, meals]) => (
                      <div key={mealType}>
                        <h3 className="font-semibold capitalize text-lg mb-2">{mealType}</h3>
                        <div className="space-y-2">
                          {meals.map((meal, index) => (
                            <div key={index} className="bg-card border rounded-lg p-3">
                              <div className="flex justify-between items-start">
                                <div>
                                  <h4 className="font-medium">{meal.name}</h4>
                                  <p className="text-sm text-muted-foreground">{meal.description}</p>
                                </div>
                                <div className="text-right">
                                  <div className="text-sm font-medium">{meal.calories} cal</div>
                                  <div className="text-xs text-muted-foreground flex items-center gap-1">
                                    <Clock className="w-3 h-3" />
                                    {meal.prepTime}min
                                  </div>
                                </div>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Shopping List */}
                  <div>
                    <h3 className="font-semibold text-lg mb-2">Shopping List</h3>
                    <div className="bg-card border rounded-lg p-4">
                      <div className="grid grid-cols-2 gap-2">
                        {currentMealPlan.shoppingList.map((item, index) => (
                          <div key={index} className="flex items-center gap-2 text-sm">
                            <CheckCircle className="w-4 h-4 text-primary" />
                            {item}
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>

                  {/* Preparation Tips */}
                  <div>
                    <h3 className="font-semibold text-lg mb-2">Preparation Tips</h3>
                    <div className="bg-card border rounded-lg p-4">
                      <ul className="space-y-2">
                        {currentMealPlan.preparationTips.map((tip, index) => (
                          <li key={index} className="flex items-start gap-2 text-sm">
                            <Info className="w-4 h-4 text-primary mt-0.5" />
                            {tip}
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/meditation-timer.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import Navbar from "@/components/layout/navbar";
import Footer from "@/components/layout/footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Slider } from "@/components/ui/slider";
import { 
  Play, 
  Pause, 
  RotateCcw, 
  Wind, 
  Brain, 
  Heart, 
  Sparkles,
  Timer as TimerIcon,
  Volume2,
  VolumeX
} from "lucide-react";
import { motion } from "framer-motion";

interface MeditationSession {
  id: string;
  name: string;
  description: string;
  icon: any;
  audioUrl?: string;
  color: string;
}

const meditationSessions: MeditationSession[] = [
  {
    id: "breathing",
    name: "Deep Breathing",
    description: "Simple breathwork to calm your mind",
    icon: Wind,
    color: "bg-blue-500",
  },
  {
    id: "mindfulness",
    name: "Mindfulness",
    description: "Present-moment awareness practice",
    icon: Brain,
    color: "bg-purple-500",
  },
  {
    id: "loving-kindness",
    name: "Loving Kindness",
    description: "Cultivate compassion and love",
    icon: Heart,
    color: "bg-pink-500",
  },
];

export default function MeditationTimer() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();

  const [selectedSession, setSelectedSession] = useState(meditationSessions[0]);
  const [duration, setDuration] = useState([10]); // in minutes
  const [timeLeft, setTimeLeft] = useState(duration[0] * 60); // in seconds
  const [isRunning, setIsRunning] = useState(false);
  const [isCompleted, setIsCompleted] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [currentPhase, setCurrentPhase] = useState("preparation");

  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Sign In Required",
        description: "Please sign in to access the meditation timer.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  // Update timer when duration changes
  useEffect(() => {
    if (!isRunning) {
      setTimeLeft(duration[0] * 60);
      setIsCompleted(false);
    }
  }, [duration, isRunning]);

  // Timer countdown logic
  useEffect(() => {
    if (isRunning && timeLeft > 0) {
      intervalRef.current = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            setIsRunning(false);
            setIsCompleted(true);
            toast({
              title: "Session Complete!",
              description: "Congratulations on completing your meditation session.",
            });
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isRunning, timeLeft, toast]);

  // Update meditation phase based on progress
  useEffect(() => {
    const progress = 1 - timeLeft / (duration[0] * 60);
    if (progress < 0.1) {
      setCurrentPhase("preparation");
    } else if (progress < 0.9) {
      setCurrentPhase("meditation");
    } else {
      setCurrentPhase("completion");
    }
  }, [timeLeft, duration]);

  const handleStart = () => {
    setIsRunning(true);
    setIsCompleted(false);
  };

  const handlePause = () => {
    setIsRunning(false);
  };

  const handleReset = () => {
    setIsRunning(false);
    setTimeLeft(duration[0] * 60);
    setIsCompleted(false);
    setCurrentPhase("preparation");
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const progress = 1 - timeLeft / (duration[0] * 60);
  const circumference = 2 * Math.PI * 120;
  const strokeDashoffset = circumference - progress * circumference;

  const getGuidanceText = () => {
    switch (currentPhase) {
      case "preparation":
        return "Find a comfortable seated position and close your eyes";
      case "meditation":
        return selectedSession.description;
      case "completion":
        return "Take a moment to notice how you feel";
      default:
        return "Ready to begin your meditation practice";
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-sage-25 flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-sage-500 border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!isAuthenticated) {
    return null; // Will redirect via useEffect
  }

  return (
    <div className="min-h-screen bg-sage-25">
      <Navbar />

      {/* Header */}
      <section className="pt-24 pb-8 wellness-gradient">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <div className="inline-flex items-center bg-sage-100 text-sage-700 px-4 py-2 rounded-full mb-6">
                <TimerIcon className="w-5 h-5 mr-2" />
                Meditation Timer
              </div>
              <h1 className="text-4xl md:text-5xl font-bold text-sage-800 mb-4">
                Guided Meditation Sessions
              </h1>
              <p className="text-xl text-sage-600 max-w-2xl mx-auto">
                Find peace and clarity with our guided meditation practices designed for every experience level.
              </p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Meditation Interface */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid lg:grid-cols-3 gap-8">
            {/* Session Selection */}
            <div className="space-y-4">
              <Card className="organic-border premium-shadow">
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Sparkles className="w-5 h-5 text-sage-600" />
                    Choose Your Practice
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  {meditationSessions.map((session) => (
                    <motion.div
                      key={session.id}
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      <Card
                        className={`cursor-pointer transition-all duration-300 ${
                          selectedSession.id === session.id
                            ? 'border-2 border-sage-500 bg-sage-50'
                            : 'border-2 border-sage-100 hover:border-sage-200 hover:bg-sage-25'
                        }`}
                        onClick={() => setSelectedSession(session)}
                      >
                        <CardContent className="p-4">
                          <div className="flex items-center space-x-3">
                            <div className={`w-10 h-10 ${session.color} rounded-xl flex items-center justify-center`}>
                              <session.icon className="w-6 h-6 text-white" />
                            </div>
                            <div className="flex-1">
                              <h4 className="font-semibold text-sage-700">{session.name}</h4>
                              <p className="text-sm text-sage-600">{session.description}</p>
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    </motion.div>
                  ))}
                </CardContent>
              </Card>

              {/* Duration Selector */}
              <Card className="organic-border premium-shadow">
                <CardHeader>
                  <CardTitle className="text-lg">Session Duration</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="text-center">
                      <span className="text-3xl font-bold text-sage-700">{duration[0]}</span>
                      <span className="text-sage-600 ml-1">minutes</span>
                    </div>
                    <Slider
                      value={duration}
                      onValueChange={setDuration}
                      max={60}
                      min={1}
                      step={1}
                      className="w-full"
                      disabled={isRunning}
                    />
                    <div className="flex justify-between text-sm text-sage-500">
                      <span>1 min</span>
                      <span>60 min</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Timer Display */}
            <div className="lg:col-span-2">
              <Card className="organic-border premium-shadow min-h-[500px] flex flex-col items-center justify-center p-8">
                {/* Timer Circle */}
                <div className="relative w-64 h-64 mb-8">
                  <svg className="w-full h-full transform -rotate-90" viewBox="0 0 256 256">
                    {/* Background circle */}
                    <circle
                      cx="128"
                      cy="128"
                      r="120"
                      stroke="hsl(var(--sage-200))"
                      strokeWidth="8"
                      fill="transparent"
                    />
                    {/* Progress circle */}
                    <circle
                      cx="128"
                      cy="128"
                      r="120"
                      stroke="hsl(var(--sage-600))"
                      strokeWidth="8"
                      fill="transparent"
                      strokeLinecap="round"
                      strokeDasharray={circumference}
                      strokeDashoffset={strokeDashoffset}
                      className="transition-all duration-300 ease-out"
                    />
                  </svg>

                  {/* Timer Content */}
                  <div className="absolute inset-8 bg-white rounded-full flex items-center justify-center">
                    <div className="text-center">
                      <div className="text-4xl font-bold text-sage-700 mb-2">
                        {formatTime(timeLeft)}
                      </div>
                      <div className={`w-12 h-12 ${selectedSession.color} rounded-full flex items-center justify-center mx-auto`}>
                        <selectedSession.icon className="w-6 h-6 text-white" />
                      </div>
                    </div>
                  </div>
                </div>

                {/* Session Info */}
                <div className="text-center mb-8">
                  <h3 className="text-2xl font-bold text-sage-700 mb-2">
                    {selectedSession.name}
                  </h3>
                  <Badge className="bg-sage-100 text-sage-700 mb-4">
                    {currentPhase.charAt(0).toUpperCase() + currentPhase.slice(1)}
                  </Badge>
                  <Card className="bg-sage-50 border-sage-200 max-w-md mx-auto">
                    <CardContent className="p-4">
                      <p className="text-sage-700 italic">{getGuidanceText()}</p>
                    </CardContent>
                  </Card>
                </div>

                {/* Controls */}
                <div className="flex items-center space-x-4">
                  {!isRunning ? (
                    <Button
                      onClick={handleStart}
                      className="bg-sage-600 hover:bg-sage-700 text-white p-4 rounded-full"
                      disabled={isCompleted && timeLeft === 0}
                    >
                      <Play className="w-6 h-6" />
                    </Button>
                  ) : (
                    <Button
                      onClick={handlePause}
                      className="bg-amber-600 hover:bg-amber-700 text-white p-4 rounded-full"
                    >
                      <Pause className="w-6 h-6" />
                    </Button>
                  )}

                  <Button
                    onClick={handleReset}
                    variant="outline"
                    className="border-sage-300 text-sage-700 hover:bg-sage-50 p-4 rounded-full"
                  >
                    <RotateCcw className="w-6 h-6" />
                  </Button>

                  <Button
                    onClick={() => setIsMuted(!isMuted)}
                    variant="outline"
                    className="border-sage-300 text-sage-700 hover:bg-sage-50 p-4 rounded-full"
                  >
                    {isMuted ? <VolumeX className="w-6 h-6" /> : <Volume2 className="w-6 h-6" />}
                  </Button>
                </div>

                {/* Completion Message */}
                {isCompleted && (
                  <motion.div
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    className="mt-6 text-center"
                  >
                    <div className="bg-green-50 border border-green-200 rounded-xl p-6">
                      <h4 className="text-lg font-semibold text-green-800 mb-2">
                        üéâ Session Complete!
                      </h4>
                      <p className="text-green-700">
                        Well done! Take a moment to notice how you feel.
                      </p>
                    </div>
                  </motion.div>
                )}
              </Card>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
}

