import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Search, Star, ExternalLink, Heart, Filter, ShoppingBag, Sparkles } from "lucide-react";
import { motion } from "framer-motion";

// Import wellness category images for visual enhancement
import fitnessImg from "@assets/Fitness_1751936986685.jpeg";
import fitness2Img from "@assets/fitness2_1751936986686.jpeg";
import healthyRecipesImg from "@assets/Healthy Recipies_1751936986687.jpeg";
import mindfulnessImg from "@assets/Mindfulness & Meditation_1751936986688.jpeg";
import naturalRemediesImg from "@assets/Natural Remidies_1751936986688.jpeg";
import nutritionImg from "@assets/Nutrition_1751936986688.jpeg";
import premiumSupplementsImg from "@assets/Premium Suppliments_1751936986689.jpeg";
import skinCareImg from "@assets/Skin Care_1751936986689.jpeg";

interface Product {
  id: number;
  name: string;
  price: string;
  description: string | null;
  category: string | null;
  rating: string | null;
  imageUrl: string | null;
  affiliateLink: string | null;
  isRecommended: boolean | null;
  tags?: string[] | null;
}

export default function WellnessPicks() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [priceRange, setPriceRange] = useState("all");

  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const categories = [
    { id: "all", name: "All Products", icon: "üõçÔ∏è", description: "All curated wellness products", image: null },
    { id: "supplements", name: "Supplements", icon: "üíä", description: "Vitamins and nutritional supplements", image: premiumSupplementsImg },
    { id: "skincare", name: "Skincare", icon: "‚ú®", description: "Natural skincare and beauty products", image: skinCareImg },
    { id: "fitness", name: "Fitness", icon: "üèãÔ∏è", description: "Exercise equipment and accessories", image: fitnessImg },
    { id: "nutrition", name: "Nutrition", icon: "ü•ó", description: "Healthy food and nutrition products", image: nutritionImg },
    { id: "wellness-tools", name: "Wellness Tools", icon: "üßò", description: "Meditation and wellness accessories", image: mindfulnessImg },
    { id: "home", name: "Home & Living", icon: "üè°", description: "Healthy home and lifestyle products", image: naturalRemediesImg }
  ];

  const priceRanges = [
    { id: "all", name: "All Prices", range: [0, Infinity] },
    { id: "under-25", name: "Under $25", range: [0, 25] },
    { id: "25-50", name: "$25 - $50", range: [25, 50] },
    { id: "50-100", name: "$50 - $100", range: [50, 100] },
    { id: "over-100", name: "Over $100", range: [100, Infinity] }
  ];

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         (product.description && product.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesCategory = selectedCategory === "all" || product.category === selectedCategory;

    const productPrice = parseFloat(product.price.replace(/[^0-9.]/g, ''));
    const selectedRange = priceRanges.find(range => range.id === priceRange);
    const matchesPrice = !selectedRange || 
                        (productPrice >= selectedRange.range[0] && productPrice <= selectedRange.range[1]);

    return matchesSearch && matchesCategory && matchesPrice;
  });

  const recommendedProducts = filteredProducts.filter(product => product.isRecommended);
  const otherProducts = filteredProducts.filter(product => !product.isRecommended);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-sage-25 flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-sage-600 border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-sage-25 pt-16">
      {/* Header Section */}
      <section className="bg-gradient-to-br from-sage-50 to-green-50 py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center max-w-4xl mx-auto"
          >
            <Badge className="bg-sage-100 text-sage-700 mb-6">
              <ShoppingBag className="w-4 h-4 mr-2" />
              Wellness Picks
            </Badge>
            <h1 className="text-4xl md:text-6xl font-bold text-sage-800 mb-6 leading-tight">
              Curated{" "}
              <span className="text-transparent bg-gradient-to-r from-sage-600 to-green-500 bg-clip-text">
                Wellness Products
              </span>
            </h1>
            <p className="text-xl text-sage-600 mb-8">
              Thoughtfully selected products to support your wellness journey. Each item is tested, 
              reviewed, and recommended by our team of wellness experts.
            </p>

            {/* Search Bar */}
            <div className="relative max-w-md mx-auto">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-sage-400 h-5 w-5" />
              <Input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10 pr-4 py-3 text-lg border-sage-200 focus:border-sage-400 rounded-xl"
              />
            </div>
          </motion.div>
        </div>
      </section>

      {/* Filters Section */}
      <section className="py-12 bg-white border-b border-sage-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Category Filter */}
          <div className="mb-8">
            <div className="text-center mb-6">
              <h2 className="text-2xl font-bold text-sage-800 mb-2">Shop by Category</h2>
              <p className="text-sage-600">Browse our curated selection by product type</p>
            </div>
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-7 gap-4">
              {categories.map((category) => (
                <motion.div
                  key={category.id}
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5 }}
                >
                  <Button
                    variant={selectedCategory === category.id ? "default" : "outline"}
                    className={`h-auto p-4 flex flex-col items-center text-center w-full ${
                      selectedCategory === category.id
                        ? "bg-sage-600 hover:bg-sage-700 text-white"
                        : "border-sage-200 text-sage-700 hover:bg-sage-50"
                    }`}
                    onClick={() => setSelectedCategory(category.id)}
                  >
                    <span className="text-2xl mb-2">{category.icon}</span>
                    <span className="font-medium text-sm">{category.name}</span>
                  </Button>
                </motion.div>
              ))}
            </div>
          </div>

          {/* Price Filter */}
          <div className="flex items-center justify-center">
            <div className="flex items-center gap-2">
              <Filter className="w-4 h-4 text-sage-600" />
              <span className="text-sage-700 font-medium mr-3">Price Range:</span>
              <div className="flex flex-wrap gap-2">
                {priceRanges.map((range) => (
                  <Button
                    key={range.id}
                    variant={priceRange === range.id ? "default" : "outline"}
                    size="sm"
                    className={priceRange === range.id
                      ? "bg-sage-600 hover:bg-sage-700 text-white"
                      : "border-sage-200 text-sage-600 hover:bg-sage-50"
                    }
                    onClick={() => setPriceRange(range.id)}
                  >
                    {range.name}
                  </Button>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Products Section */}
      <section className="py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {selectedCategory !== "all" && (
            <div className="mb-8">
              <h2 className="text-3xl font-bold text-sage-800 mb-2">
                {categories.find(cat => cat.id === selectedCategory)?.icon}{" "}
                {categories.find(cat => cat.id === selectedCategory)?.name}
              </h2>
              <p className="text-sage-600">
                {categories.find(cat => cat.id === selectedCategory)?.description}
              </p>
            </div>
          )}

          {filteredProducts.length === 0 ? (
            <div className="text-center py-16">
              <div className="text-6xl mb-4">üõçÔ∏è</div>
              <h3 className="text-2xl font-semibold text-sage-800 mb-2">No products found</h3>
              <p className="text-sage-600 mb-6">
                {searchQuery 
                  ? "Try adjusting your search terms or filters." 
                  : "We're working on adding products to this category. Check back soon!"
                }
              </p>
              <Button 
                variant="outline" 
                onClick={() => {
                  setSearchQuery("");
                  setSelectedCategory("all");
                  setPriceRange("all");
                }}
                className="border-sage-600 text-sage-600 hover:bg-sage-50"
              >
                View All Products
              </Button>
            </div>
          ) : (
            <>
              <div className="flex items-center justify-between mb-8">
                <p className="text-sage-600">
                  Showing {filteredProducts.length} product{filteredProducts.length !== 1 ? 's' : ''}
                  {searchQuery && ` for "${searchQuery}"`}
                </p>
                {(searchQuery || selectedCategory !== "all" || priceRange !== "all") && (
                  <Button 
                    variant="ghost" 
                    onClick={() => {
                      setSearchQuery("");
                      setSelectedCategory("all");
                      setPriceRange("all");
                    }}
                    className="text-sage-600 hover:bg-sage-50"
                  >
                    Clear filters
                  </Button>
                )}
              </div>

              {/* Recommended Products */}
              {recommendedProducts.length > 0 && (
                <div className="mb-16">
                  <div className="flex items-center gap-2 mb-8">
                    <Sparkles className="w-6 h-6 text-amber-500" />
                    <h2 className="text-3xl font-bold text-sage-800">Staff Picks</h2>
                    <Badge className="bg-amber-100 text-amber-700">Recommended</Badge>
                  </div>

                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {recommendedProducts.map((product, index) => (
                      <ProductCard key={product.id} product={product} index={index} isRecommended={true} />
                    ))}
                  </div>
                </div>
              )}

              {/* Other Products */}
              {otherProducts.length > 0 && (
                <div>
                  {recommendedProducts.length > 0 && (
                    <h2 className="text-3xl font-bold text-sage-800 mb-8">More Products</h2>
                  )}

                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {otherProducts.map((product, index) => (
                      <ProductCard 
                        key={product.id} 
                        product={product} 
                        index={index + recommendedProducts.length} 
                        isRecommended={false} 
                      />
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </section>
    </div>
  );
}

function ProductCard({ product, index, isRecommended }: { 
  product: Product; 
  index: number; 
  isRecommended: boolean; 
}) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, delay: index * 0.1 }}
    >
      <Card className="h-full hover:shadow-lg transition-all duration-300 cursor-pointer group border-sage-100 hover:border-sage-200">
        <div className="relative">
          <div className="aspect-square bg-gradient-to-br from-sage-100 to-green-100 rounded-t-lg flex items-center justify-center">
            {product.imageUrl ? (
              <img 
                src={product.imageUrl} 
                alt={product.name}
                className="w-full h-full object-cover rounded-t-lg"
              />
            ) : (
              <span className="text-4xl">üåø</span>
            )}
          </div>
          {isRecommended && (
            <Badge className="absolute top-3 left-3 bg-gradient-to-r from-amber-500 to-orange-500 text-white">
              <Sparkles className="w-3 h-3 mr-1" />
              Staff Pick
            </Badge>
          )}
          <Button
            variant="ghost"
            size="sm"
            className="absolute top-3 right-3 bg-white/80 hover:bg-white text-sage-600 rounded-full p-2"
          >
            <Heart className="w-4 h-4" />
          </Button>
        </div>

        <CardHeader>
          <div className="flex items-center justify-between mb-2">
            <Badge variant="secondary" className="bg-sage-100 text-sage-700">
              {product.category || "Wellness"}
            </Badge>
            {product.rating && (
              <div className="flex items-center gap-1">
                <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
                <span className="text-sm text-sage-600">{product.rating}</span>
              </div>
            )}
          </div>
          <CardTitle className="text-xl group-hover:text-sage-600 transition-colors leading-tight">
            {product.name}
          </CardTitle>
        </CardHeader>

        <CardContent>
          <CardDescription className="text-sage-600 mb-4 line-clamp-2">
            {product.description || "High-quality wellness product carefully selected by our team."}
          </CardDescription>

          <div className="flex items-center justify-between mb-4">
            <div className="text-2xl font-bold text-sage-800">
              {product.price}
            </div>
            {product.affiliateLink && (
              <Button
                size="sm"
                className="bg-sage-600 hover:bg-sage-700 text-white"
                onClick={() => window.open(product.affiliateLink!, '_blank')}
              >
                <ExternalLink className="w-4 h-4 mr-1" />
                Shop Now
              </Button>
            )}
          </div>

          {product.tags && product.tags.length > 0 && (
            <div className="flex flex-wrap gap-1">
              {product.tags.slice(0, 3).map((tag, tagIndex) => (
                <Badge 
                  key={tagIndex} 
                  variant="outline" 
                  className="text-xs border-sage-200 text-sage-600"
                >
                  {tag}
                </Badge>
              ))}
              {product.tags.length > 3 && (
                <Badge variant="outline" className="text-xs border-sage-200 text-sage-600">
                  +{product.tags.length - 3}
                </Badge>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </motion.div>
  );
}

client/src/pages/wellness-plan.tsx.content:
import { useEffect, useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Progress } from "@/components/ui/progress";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Brain, Target, Calendar, TrendingUp, Plus, Settings } from "lucide-react";

const generatePlanSchema = z.object({
  goals: z.array(z.string()).min(1, "At least one goal is required"),
  preferences: z.array(z.string()),
  fitnessLevel: z.enum(["beginner", "intermediate", "advanced"]),
  healthConditions: z.string().optional(),
});

const goalSchema = z.object({
  title: z.string().min(1, "Title is required"),
  description: z.string().optional(),
  category: z.string().min(1, "Category is required"),
  targetValue: z.number().optional(),
  dueDate: z.string().optional(),
});

type GeneratePlanFormData = z.infer<typeof generatePlanSchema>;
type GoalFormData = z.infer<typeof goalSchema>;

export default function WellnessPlan() {
  const { isAuthenticated, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isGenerateDialogOpen, setIsGenerateDialogOpen] = useState(false);
  const [isGoalDialogOpen, setIsGoalDialogOpen] = useState(false);

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!authLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, authLoading, toast]);

  const generatePlanForm = useForm<GeneratePlanFormData>({
    resolver: zodResolver(generatePlanSchema),
    defaultValues: {
      goals: [],
      preferences: [],
      fitnessLevel: "beginner",
      healthConditions: "",
    },
  });

  const goalForm = useForm<GoalFormData>({
    resolver: zodResolver(goalSchema),
    defaultValues: {
      title: "",
      description: "",
      category: "",
      targetValue: undefined,
      dueDate: "",
    },
  });

  const { data: plans = [], isLoading } = useQuery({
    queryKey: ["/api/wellness-plans"],
    enabled: isAuthenticated,
  });

  const { data: goals = [] } = useQuery({
    queryKey: ["/api/wellness-goals"],
    enabled: isAuthenticated,
  });

  const generatePlanMutation = useMutation({
    mutationFn: async (data: GeneratePlanFormData) => {
      const response = await apiRequest("POST", "/api/generate-wellness-plan", data);
      return response;
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Your personalized wellness plan has been generated!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/wellness-plans"] });
      setIsGenerateDialogOpen(false);
      generatePlanForm.reset();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to generate wellness plan. Please try again.",
        variant: "destructive",
      });
    },
  });

  const createGoalMutation = useMutation({
    mutationFn: async (data: GoalFormData) => {
      const response = await apiRequest("POST", "/api/wellness-goals", data);
      return response;
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Goal created successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/wellness-goals"] });
      setIsGoalDialogOpen(false);
      goalForm.reset();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create goal. Please try again.",
        variant: "destructive",
      });
    },
  });

  const goalOptions = [
    "Weight Loss",
    "Muscle Gain",
    "Improved Endurance",
    "Better Sleep",
    "Stress Reduction",
    "Healthy Eating",
    "Mental Clarity",
    "Energy Boost",
  ];

  const preferenceOptions = [
    "Home Workouts",
    "Gym Sessions",
    "Outdoor Activities",
    "Yoga/Meditation",
    "Meal Planning",
    "Supplement Guidance",
    "Sleep Optimization",
    "Mindfulness Practices",
  ];

  if (authLoading || !isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Wellness Plan</h1>
          <p className="text-gray-600 dark:text-gray-300 mt-2">
            Your personalized journey to better health and wellness
          </p>
        </div>
        <div className="flex gap-3">
          <Dialog open={isGoalDialogOpen} onOpenChange={setIsGoalDialogOpen}>
            <DialogTrigger asChild>
              <Button variant="outline">
                <Target className="h-4 w-4 mr-2" />
                Add Goal
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create New Goal</DialogTitle>
                <DialogDescription>
                  Set a specific wellness goal to track your progress.
                </DialogDescription>
              </DialogHeader>
              <Form {...goalForm}>
                <form onSubmit={goalForm.handleSubmit((data) => createGoalMutation.mutate(data))} className="space-y-4">
                  <FormField
                    control={goalForm.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Goal Title</FormLabel>
                        <FormControl>
                          <Input placeholder="e.g., Lose 10 pounds" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={goalForm.control}
                    name="category"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Category</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select a category" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="fitness">Fitness</SelectItem>
                            <SelectItem value="nutrition">Nutrition</SelectItem>
                            <SelectItem value="mental-health">Mental Health</SelectItem>
                            <SelectItem value="sleep">Sleep</SelectItem>
                            <SelectItem value="habits">Habits</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={goalForm.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description (Optional)</FormLabel>
                        <FormControl>
                          <Textarea placeholder="Describe your goal in detail..." {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={goalForm.control}
                    name="targetValue"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Target Value (Optional)</FormLabel>
                        <FormControl>
                          <Input 
                            type="number" 
                            placeholder="e.g., 10 (for 10 pounds)" 
                            {...field}
                            onChange={(e) => field.onChange(e.target.value ? parseFloat(e.target.value) : undefined)}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={goalForm.control}
                    name="dueDate"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Target Date (Optional)</FormLabel>
                        <FormControl>
                          <Input type="date" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <Button 
                    type="submit" 
                    disabled={createGoalMutation.isPending}
                    className="w-full"
                  >
                    {createGoalMutation.isPending ? "Creating..." : "Create Goal"}
                  </Button>
                </form>
              </Form>
            </DialogContent>
          </Dialog>

          <Dialog open={isGenerateDialogOpen} onOpenChange={setIsGenerateDialogOpen}>
            <DialogTrigger asChild>
              <Button>
                <Brain className="h-4 w-4 mr-2" />
                Generate AI Plan
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Generate Personalized Wellness Plan</DialogTitle>
                <DialogDescription>
                  Tell us about your goals and preferences, and our AI will create a customized wellness plan for you.
                </DialogDescription>
              </DialogHeader>
              <Form {...generatePlanForm}>
                <form onSubmit={generatePlanForm.handleSubmit((data) => generatePlanMutation.mutate(data))} className="space-y-6">
                  <FormField
                    control={generatePlanForm.control}
                    name="goals"
                    render={() => (
                      <FormItem>
                        <FormLabel>Goals (Select at least one)</FormLabel>
                        <div className="grid grid-cols-2 gap-3">
                          {goalOptions.map((goal) => (
                            <FormField
                              key={goal}
                              control={generatePlanForm.control}
                              name="goals"
                              render={({ field }) => (
                                <FormItem className="flex items-center space-x-3">
                                  <FormControl>
                                    <Checkbox
                                      checked={field.value?.includes(goal)}
                                      onCheckedChange={(checked) => {
                                        const updatedValue = checked
                                          ? [...(field.value || []), goal]
                                          : field.value?.filter((value) => value !== goal) || [];
                                        field.onChange(updatedValue);
                                      }}
                                    />
                                  </FormControl>
                                  <FormLabel className="text-sm font-normal">
                                    {goal}
                                  </FormLabel>
                                </FormItem>
                              )}
                            />
                          ))}
                        </div>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={generatePlanForm.control}
                    name="preferences"
                    render={() => (
                      <FormItem>
                        <FormLabel>Preferences (Optional)</FormLabel>
                        <div className="grid grid-cols-2 gap-3">
                          {preferenceOptions.map((preference) => (
                            <FormField
                              key={preference}
                              control={generatePlanForm.control}
                              name="preferences"
                              render={({ field }) => (
                                <FormItem className="flex items-center space-x-3">
                                  <FormControl>
                                    <Checkbox
                                      checked={field.value?.includes(preference)}
                                      onCheckedChange={(checked) => {
                                        const updatedValue = checked
                                          ? [...(field.value || []), preference]
                                          : field.value?.filter((value) => value !== preference) || [];
                                        field.onChange(updatedValue);
                                      }}
                                    />
                                  </FormControl>
                                  <FormLabel className="text-sm font-normal">
                                    {preference}
                                  </FormLabel>
                                </FormItem>
                              )}
                            />
                          ))}
                        </div>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={generatePlanForm.control}
                    name="fitnessLevel"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Fitness Level</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="beginner">Beginner</SelectItem>
                            <SelectItem value="intermediate">Intermediate</SelectItem>
                            <SelectItem value="advanced">Advanced</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={generatePlanForm.control}
                    name="healthConditions"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Health Conditions (Optional)</FormLabel>
                        <FormControl>
                          <Textarea 
                            placeholder="Please mention any health conditions or limitations..."
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <Button 
                    type="submit" 
                    disabled={generatePlanMutation.isPending}
                    className="w-full"
                  >
                    {generatePlanMutation.isPending ? "Generating..." : "Generate Plan"}
                  </Button>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      <Tabs defaultValue="plans" className="space-y-6">
        <TabsList>
          <TabsTrigger value="plans">My Plans</TabsTrigger>
          <TabsTrigger value="goals">Goals</TabsTrigger>
          <TabsTrigger value="progress">Progress</TabsTrigger>
        </TabsList>

        <TabsContent value="plans" className="space-y-6">
          {isLoading ? (
            <div className="flex items-center justify-center py-12">
              <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
            </div>
          ) : plans.length === 0 ? (
            <Card>
              <CardContent className="text-center py-12">
                <Brain className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                  No wellness plans yet
                </h3>
                <p className="text-gray-600 dark:text-gray-300 mb-4">
                  Get started by generating your first AI-powered wellness plan.
                </p>
                <Button onClick={() => setIsGenerateDialogOpen(true)}>
                  <Brain className="h-4 w-4 mr-2" />
                  Generate Your First Plan
                </Button>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-6">
              {plans.map((plan: any) => (
                <Card key={plan.id}>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <div>
                        <CardTitle>{plan.title}</CardTitle>
                        <CardDescription>{plan.description}</CardDescription>
                      </div>
                      <Badge variant={plan.status === "active" ? "default" : "secondary"}>
                        {plan.status}
                      </Badge>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      <div>
                        <div className="flex items-center justify-between mb-2">
                          <span className="text-sm font-medium">Progress</span>
                          <span className="text-sm text-gray-500">{plan.completionPercentage || 0}%</span>
                        </div>
                        <Progress value={plan.completionPercentage || 0} />
                      </div>

                      {plan.goals && plan.goals.length > 0 && (
                        <div>
                          <h4 className="text-sm font-medium mb-2">Goals</h4>
                          <div className="flex flex-wrap gap-2">
                            {plan.goals.map((goal: string, index: number) => (
                              <Badge key={index} variant="outline">{goal}</Badge>
                            ))}
                          </div>
                        </div>
                      )}

                      {plan.duration && (
                        <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                          <Calendar className="h-4 w-4 mr-1" />
                          Duration: {plan.duration}
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="goals" className="space-y-6">
          {goals.length === 0 ? (
            <Card>
              <CardContent className="text-center py-12">
                <Target className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                  No goals set yet
                </h3>
                <p className="text-gray-600 dark:text-gray-300 mb-4">
                  Set specific wellness goals to track your progress.
                </p>
                <Button onClick={() => setIsGoalDialogOpen(true)}>
                  <Target className="h-4 w-4 mr-2" />
                  Add Your First Goal
                </Button>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-4">
              {goals.map((goal: any) => (
                <Card key={goal.id}>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <div>
                        <CardTitle className="text-lg">{goal.title}</CardTitle>
                        {goal.description && (
                          <CardDescription>{goal.description}</CardDescription>
                        )}
                      </div>
                      <Badge variant={goal.isCompleted ? "default" : "secondary"}>
                        {goal.category}
                      </Badge>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <div className="flex items-center justify-between">
                      {goal.targetValue && (
                        <span className="text-sm text-gray-600 dark:text-gray-300">
                          Target: {goal.targetValue} {goal.unit || ""}
                        </span>
                      )}
                      {goal.dueDate && (
                        <span className="text-sm text-gray-600 dark:text-gray-300">
                          Due: {new Date(goal.dueDate).toLocaleDateString()}
                        </span>
                      )}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="progress" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <TrendingUp className="h-5 w-5 mr-2" />
                Progress Overview
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-center py-8">
                <p className="text-gray-600 dark:text-gray-300">
                  Progress tracking coming soon...
                </p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

