import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { 
  Handshake, 
  TrendingUp, 
  DollarSign, 
  Target,
  Clock,
  CheckCircle,
  AlertCircle,
  Zap,
  Brain,
  Star,
  Gift,
  Users
} from 'lucide-react';

interface NegotiationTarget {
  id: string;
  merchant: string;
  currentCommission: number;
  targetCommission: number;
  volume: number;
  leverage: number;
  confidence: number;
  strategy: string;
  status: 'pending' | 'negotiating' | 'accepted' | 'rejected' | 'counter_offered';
  estimatedIncrease: number;
  negotiationHistory: string[];
}

interface ExclusiveDeal {
  id: string;
  merchant: string;
  product: string;
  exclusivityType: 'early_access' | 'exclusive_discount' | 'unique_bundle';
  value: number;
  duration: string;
  status: 'discovered' | 'negotiating' | 'secured' | 'expired';
  competitiveAdvantage: string;
}

interface PartnershipOpportunity {
  id: string;
  merchant: string;
  opportunityType: 'content_collaboration' | 'co_marketing' | 'product_development';
  potentialValue: number;
  requirements: string[];
  aiRecommendation: string;
  priority: 'low' | 'medium' | 'high';
}

export function AutonomousNegotiation() {
  const [autoNegotiationEnabled, setAutoNegotiationEnabled] = useState(true);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch negotiation targets
  const { data: negotiationTargets = [] } = useQuery({
    queryKey: ['/api/negotiation/targets'],
    refetchInterval: 60000,
    select: (data: any) => data?.data || []
  });

  // Fetch exclusive deals
  const { data: exclusiveDeals = [] } = useQuery({
    queryKey: ['/api/negotiation/exclusive-deals'],
    select: (data: any) => data?.data || []
  });

  // Fetch partnership opportunities
  const { data: partnerships = [] } = useQuery({
    queryKey: ['/api/negotiation/partnerships'],
    select: (data: any) => data?.data || []
  });

  // Auto-negotiate commission rates
  const autoNegotiate = useMutation({
    mutationFn: (targetId: string) => 
      apiRequest('POST', `/api/negotiation/auto-negotiate/${targetId}`),
    onSuccess: () => {
      toast({
        title: 'Negotiation Initiated',
        description: 'AI is autonomously negotiating better commission rates'
      });
      queryClient.invalidateQueries({ queryKey: ['/api/negotiation/targets'] });
    }
  });

  // Hunt for exclusive deals
  const huntExclusiveDeals = useMutation({
    mutationFn: () => apiRequest('POST', '/api/negotiation/hunt-exclusives'),
    onSuccess: () => {
      toast({
        title: 'Deal Hunting Active',
        description: 'AI is scanning for exclusive partnership opportunities'
      });
      queryClient.invalidateQueries({ queryKey: ['/api/negotiation/exclusive-deals'] });
    }
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'accepted': case 'secured': return 'text-green-600';
      case 'negotiating': case 'discovered': return 'text-blue-600';
      case 'pending': return 'text-yellow-600';
      case 'rejected': case 'expired': return 'text-red-600';
      case 'counter_offered': return 'text-purple-600';
      default: return 'text-gray-600';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'accepted': case 'secured': return <CheckCircle className="h-4 w-4" />;
      case 'negotiating': case 'discovered': return <Clock className="h-4 w-4" />;
      case 'pending': return <AlertCircle className="h-4 w-4" />;
      case 'rejected': case 'expired': return <AlertCircle className="h-4 w-4" />;
      default: return <Clock className="h-4 w-4" />;
    }
  };

  return (
    <div className="space-y-6">
      {/* Autonomous Negotiation Overview */}
      <Card className="border-2 border-green-200 bg-gradient-to-r from-green-50 to-emerald-50">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Handshake className="h-5 w-5 text-green-600" />
            Autonomous Negotiation Engine
            <Badge variant="outline" className="ml-2">
              +1% Autonomy Gain
            </Badge>
          </CardTitle>
          <CardDescription>
            AI-powered commission optimization and partnership building
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-5 gap-4 mb-6">
            <div className="text-center">
              <div className="text-3xl font-bold text-green-600">23</div>
              <div className="text-xs text-muted-foreground">Active Negotiations</div>
            </div>

            <div className="text-center">
              <div className="text-3xl font-bold text-blue-600">+2.3%</div>
              <div className="text-xs text-muted-foreground">Avg Commission Increase</div>
            </div>

            <div className="text-center">
              <div className="text-3xl font-bold text-purple-600">$1,247</div>
              <div className="text-xs text-muted-foreground">Monthly Gain</div>
            </div>

            <div className="text-center">
              <div className="text-3xl font-bold text-orange-600">87%</div>
              <div className="text-xs text-muted-foreground">Success Rate</div>
            </div>

            <div className="text-center">
              <div className="text-3xl font-bold text-indigo-600">12</div>
              <div className="text-xs text-muted-foreground">Exclusive Deals</div>
            </div>
          </div>

          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <Switch
                checked={autoNegotiationEnabled}
                onCheckedChange={setAutoNegotiationEnabled}
              />
              <Label>Auto-Negotiation</Label>
            </div>

            <Button 
              onClick={() => huntExclusiveDeals.mutate()}
              disabled={huntExclusiveDeals.isPending}
              className="flex items-center gap-2"
            >
              <Gift className="h-4 w-4" />
              Hunt Exclusive Deals
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Commission Rate Negotiations */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-4 w-4" />
            Commission Rate Optimization
          </CardTitle>
          <CardDescription>
            AI-driven negotiations for higher commission rates
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {negotiationTargets.map((target: NegotiationTarget) => (
              <div key={target.id} className="flex items-center gap-4 p-4 border rounded-lg hover:bg-muted/30">
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-1">
                    <span className="font-medium">{target.merchant}</span>
                    {getStatusIcon(target.status)}
                    <Badge variant="outline" className="text-xs">
                      {target.strategy}
                    </Badge>
                  </div>

                  <div className="text-sm text-muted-foreground mb-2">
                    Current: {target.currentCommission}% ‚Üí Target: {target.targetCommission}% 
                    (+{((target.targetCommission - target.currentCommission) * 100).toFixed(1)}%)
                  </div>

                  <div className="flex items-center gap-4 text-xs">
                    <span>Volume: ${target.volume.toLocaleString()}</span>
                    <span>Leverage: {(target.leverage * 100).toFixed(0)}%</span>
                    <span>Confidence: {(target.confidence * 100).toFixed(0)}%</span>
                  </div>
                </div>

                <div className="text-right">
                  <div className={`text-sm font-medium ${getStatusColor(target.status)}`}>
                    {target.status.replace('_', ' ').toUpperCase()}
                  </div>
                  <div className="text-lg font-bold text-green-600">
                    +${target.estimatedIncrease}/mo
                  </div>
                  {target.status === 'pending' && (
                    <Button
                      size="sm"
                      onClick={() => autoNegotiate.mutate(target.id)}
                      disabled={autoNegotiate.isPending}
                      className="mt-2"
                    >
                      <Zap className="h-3 w-3" />
                    </Button>
                  )}
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Exclusive Deals */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Star className="h-4 w-4" />
            Exclusive Deal Pipeline
          </CardTitle>
          <CardDescription>
            Secured and potential exclusive partnership opportunities
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {exclusiveDeals.map((deal: ExclusiveDeal) => (
              <div key={deal.id} className="p-4 border rounded-lg bg-gradient-to-br from-yellow-50 to-orange-50">
                <div className="flex items-center justify-between mb-3">
                  <h4 className="font-medium">{deal.product}</h4>
                  <Badge variant={deal.status === 'secured' ? 'default' : 'secondary'}>
                    {deal.status.replace('_', ' ')}
                  </Badge>
                </div>

                <div className="space-y-2 mb-4">
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Merchant:</span>
                    <span>{deal.merchant}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Type:</span>
                    <span>{deal.exclusivityType.replace('_', ' ')}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Value:</span>
                    <span className="text-green-600 font-medium">${deal.value}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Duration:</span>
                    <span>{deal.duration}</span>
                  </div>
                </div>

                <div className="text-xs text-blue-600">
                  Advantage: {deal.competitiveAdvantage}
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Partnership Building */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Users className="h-4 w-4" />
            Partnership Automation
          </CardTitle>
          <CardDescription>
            AI-identified partnership opportunities and relationship building
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {partnerships.map((partnership: PartnershipOpportunity) => (
              <div key={partnership.id} className="flex items-center gap-4 p-4 border rounded-lg hover:bg-muted/30">
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-1">
                    <span className="font-medium">{partnership.merchant}</span>
                    <Badge variant="outline" className="text-xs">
                      {partnership.opportunityType.replace('_', ' ')}
                    </Badge>
                    <Badge variant={partnership.priority === 'high' ? 'default' : 'secondary'}>
                      {partnership.priority} priority
                    </Badge>
                  </div>

                  <div className="text-sm text-muted-foreground mb-2">
                    Potential Value: ${partnership.potentialValue.toLocaleString()}
                  </div>

                  <div className="text-xs text-blue-600 mb-2">
                    AI Recommendation: {partnership.aiRecommendation}
                  </div>

                  <div className="text-xs text-muted-foreground">
                    Requirements: {partnership.requirements.slice(0, 2).join(' ‚Ä¢ ')}
                  </div>
                </div>

                <div className="text-right">
                  <Button size="sm" variant="outline">
                    <Brain className="h-3 w-3 mr-1" />
                    AI Initiate
                  </Button>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Negotiation Analytics */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Target className="h-4 w-4" />
            Negotiation Performance Analytics
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-3 gap-6">
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">87%</div>
              <div className="text-sm text-muted-foreground mb-2">Success Rate</div>
              <Progress value={87} className="h-2" />
            </div>

            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">+$3,247</div>
              <div className="text-sm text-muted-foreground mb-2">Monthly Revenue Increase</div>
              <div className="text-xs text-green-600">+18% vs last month</div>
            </div>

            <div className="text-center">
              <div className="text-2xl font-bold text-purple-600">2.3 days</div>
              <div className="text-sm text-muted-foreground mb-2">Avg Negotiation Time</div>
              <div className="text-xs text-green-600">-40% faster than manual</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/admin/BlogManageme.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Eye, 
  Sparkles, 
  Loader2,
  Calendar,
  User,
  Tag,
  FileText,
  Wand2,
  AlertTriangle
} from "lucide-react";
import { BlogPost } from "@shared/schema";
import { blogPostSchema, type BlogPostFormData } from "@shared/validation/admin";
import type { BlogPostForm } from "@shared/types/admin";
import { useAdminFilters } from "@/hooks/useAdminFilters";
import { useBulkOperations } from "@/hooks/useBulkOperations";
import { SearchAndFilters } from "./SearchAndFilters";
import { BulkOperations } from "./BulkOperations";

export default function BlogManagement() {
  const [selectedPost, setSelectedPost] = useState<BlogPost | null>(null);
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [bulkCreateCount, setBulkCreateCount] = useState(3);
  const [validationErrors, setValidationErrors] = useState<Record<string, string>>({});

  const [newPost, setNewPost] = useState<BlogPostForm>({
    title: '',
    content: '',
    excerpt: '',
    category: '',
    tags: '',
    published: false,
    featured: false,
    metaDescription: '',
    slug: ''
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: blogPosts, isLoading } = useQuery({
    queryKey: ['/api/blog/posts'],
  });

  // Enhanced filtering and search
  const {
    filters,
    filteredData: filteredPosts,
    updateFilter,
    resetFilters,
    totalCount,
    filteredCount
  } = useAdminFilters({
    data: (blogPosts as BlogPost[]) || [],
    searchFields: ['title', 'content', 'excerpt'],
    categoryField: 'category',
    statusField: 'isPublished'
  });

  // Bulk operations
  const bulkOps = useBulkOperations({
    queryKey: ['/api/blog/posts'],
    endpoint: '/api/blog/posts'
  });

  // Categories for filtering
  const categories = [
    { value: 'wellness', label: 'Wellness' },
    { value: 'nutrition', label: 'Nutrition' },
    { value: 'fitness', label: 'Fitness' },
    { value: 'mindfulness', label: 'Mindfulness' },
    { value: 'lifestyle', label: 'Lifestyle' }
  ];

  // Form validation function
  const validateForm = (): boolean => {
    try {
      blogPostSchema.parse(newPost);
      setValidationErrors({});
      return true;
    } catch (error: any) {
      const errors: Record<string, string> = {};
      if (error.errors) {
        error.errors.forEach((err: any) => {
          errors[err.path[0]] = err.message;
        });
      }
      setValidationErrors(errors);
      toast({
        title: "Validation Error",
        description: "Please fix the form errors before submitting",
        variant: "destructive"
      });
      return false;
    }
  };

  const createPostMutation = useMutation({
    mutationFn: async (post: BlogPostFormData) => {
      if (!validateForm()) {
        throw new Error('Form validation failed');
      }
      const response = await apiRequest('POST', '/api/blog/posts', post);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/blog/posts'] });
      setIsCreateDialogOpen(false);
      resetForm();
      bulkOps.clearSelection();
      toast({
        title: "Success",
        description: "Blog post created successfully",
      });
    },
    onError: (error) => {
      if (error.message !== 'Form validation failed') {
        toast({
          title: "Error",
          description: error.message,
          variant: "destructive",
        });
      }
    },
  });

  const updatePostMutation = useMutation({
    mutationFn: async (post: any) => {
      const response = await apiRequest('PUT', `/api/blog/posts/${selectedPost?.id}`, post);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/blog/posts'] });
      setIsEditDialogOpen(false);
      setSelectedPost(null);
      resetForm();
      toast({
        title: "Success",
        description: "Blog post updated successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deletePostMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest('DELETE', `/api/blog/posts/${id}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/blog/posts'] });
      toast({
        title: "Success",
        description: "Blog post deleted successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const generateAIPostMutation = useMutation({
    mutationFn: async (data: { title: string; category: string; autoPublish: boolean }) => {
      const response = await apiRequest('POST', '/api/admin/generate-blog-post', data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/blog/posts'] });
      toast({
        title: "Success",
        description: "AI blog post generated successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setNewPost({
      title: '',
      content: '',
      excerpt: '',
      category: '',
      tags: '',
      published: false,
      featured: false,
      metaDescription: '',
      slug: ''
    });
    setValidationErrors({});
  };

  const handleEdit = (post: BlogPost) => {
    setSelectedPost(post);
    setNewPost({
      title: post.title,
      content: post.content,
      excerpt: post.excerpt || '',
      category: post.category || '',
      tags: post.tags?.join(', ') || '',
      published: post.isPublished || false,
      featured: false, // This field might not exist in the schema
      metaDescription: '',
      slug: post.slug
    });
    setIsEditDialogOpen(true);
  };

  const handleDelete = (id: number) => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      deletePostMutation.mutate(id);
    }
  };

  const handleGenerateAI = async () => {
    if (!newPost.title || !newPost.category) {
      toast({
        title: "Error",
        description: "Please provide both title and category for AI generation",
        variant: "destructive",
      });
      return;
    }

    setIsAIGenerating(true);
    try {
      await generateAIPostMutation.mutateAsync({
        title: newPost.title,
        category: newPost.category,
        autoPublish: newPost.published
      });
    } finally {
      setIsAIGenerating(false);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <Loader2 className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Blog Management</h2>
          <p className="text-muted-foreground">Create, edit, and manage blog posts</p>
        </div>
        <div className="flex gap-2">
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button>
                <Plus className="h-4 w-4 mr-2" />
                Create Post
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Create New Blog Post</DialogTitle>
                <DialogDescription>
                  Create a new blog post manually or use AI to generate content
                </DialogDescription>
              </DialogHeader>
              <BlogPostForm
                post={newPost}
                setPost={setNewPost}
                onSubmit={() => createPostMutation.mutate(newPost)}
                onAIGenerate={handleGenerateAI}
                isSubmitting={createPostMutation.isPending}
                isAIGenerating={isAIGenerating}
                validationErrors={validationErrors}
              />
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Enhanced Search and Filtering */}
      <SearchAndFilters
        filters={filters}
        onFilterChange={updateFilter}
        onResetFilters={resetFilters}
        categories={categories}
        totalCount={totalCount}
        filteredCount={filteredCount}
        className="bg-sage-50/50 p-4 rounded-lg border"
      />

      {/* Bulk Operations */}
      <BulkOperations
        selectedItems={bulkOps.selectedItems}
        totalItems={filteredPosts.length}
        isProcessing={bulkOps.isProcessing}
        onSelectAll={() => bulkOps.handleSelectAll(filteredPosts.map(p => p.id))}
        onBulkAction={bulkOps.handleBulkAction}
        onClearSelection={bulkOps.clearSelection}
      />

      {/* Enhanced Blog Posts Grid */}
      <div className="grid gap-4">
        {filteredPosts.length === 0 ? (
          <Card className="text-center py-12">
            <CardContent>
              <FileText className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
              <h3 className="text-lg font-semibold mb-2">No blog posts found</h3>
              <p className="text-muted-foreground mb-4">
                {totalCount === 0 
                  ? "Get started by creating your first blog post" 
                  : "Try adjusting your search or filter criteria"
                }
              </p>
              {totalCount === 0 && (
                <Button onClick={() => setIsCreateDialogOpen(true)}>
                  <Plus className="h-4 w-4 mr-2" />
                  Create First Post
                </Button>
              )}
            </CardContent>
          </Card>
        ) : (
          filteredPosts.map((post: BlogPost) => (
            <Card key={post.id} className="hover:shadow-md transition-shadow">
              <CardHeader>
                <div className="flex justify-between items-start">
                  <div className="flex items-start gap-3 flex-1">
                    <Checkbox
                      checked={bulkOps.selectedItems.includes(post.id)}
                      onCheckedChange={() => bulkOps.handleSelectItem(post.id)}
                      aria-label={`Select ${post.title}`}
                    />
                    <div className="flex-1">
                      <CardTitle className="text-lg hover:text-sage-600 cursor-pointer">
                        {post.title}
                      </CardTitle>
                      <CardDescription className="mt-1">
                        {post.excerpt || post.content.substring(0, 150) + '...'}
                      </CardDescription>
                    </div>
                  </div>
                  <div className="flex gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleEdit(post)}
                      className="hover:bg-sage-50"
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleDelete(post.id)}
                      className="hover:bg-red-50 hover:text-red-600"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <div className="flex flex-wrap gap-2 mb-4">
                  <Badge variant={post.published ? "default" : "secondary"}>
                    {post.published ? "Published" : "Draft"}
                  </Badge>
                  {post.featured && (
                    <Badge variant="outline" className="border-yellow-200 text-yellow-700">
                      <Star className="h-3 w-3 mr-1" />
                      Featured
                    </Badge>
                  )}
                  <Badge variant="outline" className="bg-sage-50">
                    {post.category}
                  </Badge>
                  {post.tags && post.tags.length > 0 && (
                    <Badge variant="outline" className="bg-blue-50">
                      <Tag className="h-3 w-3 mr-1" />
                      {post.tags.length} tags
                    </Badge>
                  )}
                </div>
                <div className="flex items-center justify-between text-sm text-muted-foreground">
                  <div className="flex items-center gap-4">
                    <div className="flex items-center gap-1">
                      <Calendar className="h-4 w-4" />
                      {post.createdAt ? new Date(post.createdAt).toLocaleDateString() : 'No date'}
                    </div>
                    <div className="flex items-center gap-1">
                      <User className="h-4 w-4" />
                      Author {post.authorId}
                    </div>
                  </div>
                  {!post.isPublished && (
                    <div className="flex items-center gap-1 text-orange-600">
                      <AlertTriangle className="h-4 w-4" />
                      Needs Review
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit Blog Post</DialogTitle>
            <DialogDescription>
              Update the blog post details
            </DialogDescription>
          </DialogHeader>
          <BlogPostForm
            post={newPost}
            setPost={setNewPost}
            onSubmit={() => updatePostMutation.mutate(newPost)}
            isSubmitting={updatePostMutation.isPending}
            isEditing={true}
            validationErrors={validationErrors}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

function BlogPostForm({ 
  post, 
  setPost, 
  onSubmit, 
  onAIGenerate, 
  isSubmitting, 
  isAIGenerating, 
  isEditing = false,
  validationErrors = {}
}: {
  post: BlogPostForm;
  setPost: (post: BlogPostForm) => void;
  onSubmit: () => void;
  onAIGenerate?: () => void;
  isSubmitting: boolean;
  isAIGenerating?: boolean;
  isEditing?: boolean;
  validationErrors?: Record<string, string>;
}) {
  return (
    <div className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="title">Title</Label>
          <Input
            id="title"
            value={post.title}
            onChange={(e) => setPost({ ...post, title: e.target.value })}
            placeholder="Enter post title"
            className={validationErrors.title ? "border-red-500" : ""}
          />
          {validationErrors.title && (
            <p className="text-red-500 text-sm mt-1 flex items-center gap-1">
              <AlertTriangle className="h-3 w-3" />
              {validationErrors.title}
            </p>
          )}
        </div>
        <div>
          <Label htmlFor="category">Category</Label>
          <Select value={post.category} onValueChange={(value) => setPost({ ...post, category: value })}>
            <SelectTrigger className={validationErrors.category ? "border-red-500" : ""}>
              <SelectValue placeholder="Select category" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="fitness">Fitness</SelectItem>
              <SelectItem value="nutrition">Nutrition</SelectItem>
              <SelectItem value="mindfulness">Mindfulness</SelectItem>
              <SelectItem value="wellness">Wellness</SelectItem>
              <SelectItem value="lifestyle">Lifestyle</SelectItem>
            </SelectContent>
          </Select>
          {validationErrors.category && (
            <p className="text-red-500 text-sm mt-1 flex items-center gap-1">
              <AlertTriangle className="h-3 w-3" />
              {validationErrors.category}
            </p>
          )}
        </div>
      </div>

      <div>
        <Label htmlFor="excerpt">Excerpt</Label>
        <Textarea
          id="excerpt"
          value={post.excerpt}
          onChange={(e) => setPost({ ...post, excerpt: e.target.value })}
          placeholder="Brief description of the post"
          rows={2}
        />
      </div>

      <div>
        <Label htmlFor="content">Content</Label>
        <Textarea
          id="content"
          value={post.content}
          onChange={(e) => setPost({ ...post, content: e.target.value })}
          placeholder="Post content"
          rows={8}
        />
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="tags">Tags (comma-separated)</Label>
          <Input
            id="tags"
            value={post.tags}
            onChange={(e) => setPost({ ...post, tags: e.target.value })}
            placeholder="wellness, health, fitness"
          />
        </div>
        <div>
          <Label htmlFor="slug">Slug</Label>
          <Input
            id="slug"
            value={post.slug}
            onChange={(e) => setPost({ ...post, slug: e.target.value })}
            placeholder="post-url-slug"
          />
        </div>
      </div>

      <div>
        <Label htmlFor="metaDescription">Meta Description</Label>
        <Textarea
          id="metaDescription"
          value={post.metaDescription}
          onChange={(e) => setPost({ ...post, metaDescription: e.target.value })}
          placeholder="SEO meta description"
          rows={2}
        />
      </div>

      <div className="flex items-center gap-4">
        <div className="flex items-center space-x-2">
          <Switch
            id="published"
            checked={post.published}
            onCheckedChange={(checked) => setPost({ ...post, published: checked })}
          />
          <Label htmlFor="published">Published</Label>
        </div>
        <div className="flex items-center space-x-2">
          <Switch
            id="featured"
            checked={post.featured}
            onCheckedChange={(checked) => setPost({ ...post, featured: checked })}
          />
          <Label htmlFor="featured">Featured</Label>
        </div>
      </div>

      <div className="flex gap-2">
        <Button onClick={onSubmit} disabled={isSubmitting}>
          {isSubmitting && <Loader2 className="h-4 w-4 mr-2 animate-spin" />}
          {isEditing ? 'Update Post' : 'Create Post'}
        </Button>

        {!isEditing && onAIGenerate && (
          <Button
            variant="outline"
            onClick={onAIGenerate}
            disabled={isAIGenerating || !post.title || !post.category}
          >
            {isAIGenerating ? (
              <Loader2 className="h-4 w-4 mr-2 animate-spin" />
            ) : (
              <Wand2 className="h-4 w-4 mr-2" />
            )}
            Generate with AI
          </Button>
        )}
      </div>
    </div>
  );
}

client/src/components/admin/BulkImportMo.content:
import { useState } from 'react';
import { useQueryClient } from '@tanstack/react-query';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Progress } from '@/components/ui/progress';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Upload, FileText, CheckCircle, AlertCircle, Download } from 'lucide-react';

interface BulkImportModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export function BulkImportModal({ isOpen, onClose }: BulkImportModalProps) {
  const [file, setFile] = useState<File | null>(null);
  const [csvData, setCsvData] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [results, setResults] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = event.target.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);

      // Read CSV content
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target?.result as string;
        setCsvData(content);
      };
      reader.readAsText(selectedFile);
    }
  };

  const processBulkImport = async () => {
    if (!csvData && !file) {
      toast({
        title: 'Error',
        description: 'Please upload a CSV file or paste CSV data',
        variant: 'destructive'
      });
      return;
    }

    setIsProcessing(true);
    setProgress(0);

    try {
      // Parse CSV data into affiliate links
      const lines = csvData.split('\n').filter(line => line.trim());
      const headers = lines[0].split(',').map(h => h.trim().toLowerCase());
      const linkData = [];

      for (let i = 1; i < lines.length; i++) {
        const values = lines[i].split(',').map(v => v.trim());
        const link: any = {};

        headers.forEach((header, index) => {
          if (values[index]) {
            switch (header) {
              case 'url':
              case 'affiliate url':
              case 'link':
                link.url = values[index];
                break;
              case 'merchant':
              case 'store':
                link.merchant = values[index];
                break;
              case 'product name':
              case 'name':
              case 'title':
                link.productName = values[index];
                break;
              case 'category':
                link.category = values[index];
                break;
              case 'commission':
              case 'commission %':
                link.commission = values[index];
                break;
              case 'description':
                link.description = values[index];
                break;
              case 'image url':
              case 'image':
                link.imageUrl = values[index];
                break;
            }
          }
        });

        if (link.url && link.productName) {
          linkData.push(link);
        }
      }

      // Process links in batches
      const batchSize = 5;
      const results = [];

      for (let i = 0; i < linkData.length; i += batchSize) {
        const batch = linkData.slice(i, i + batchSize);

        try {
          const response = await apiRequest('POST', '/api/affiliate-links/bulk', {
            links: batch
          });
          const result = await response.json();
          results.push(...result.data);

          setProgress(Math.round(((i + batch.length) / linkData.length) * 100));

          // Small delay to prevent overwhelming the API
          await new Promise(resolve => setTimeout(resolve, 500));
        } catch (error: any) {
          console.error('Batch processing error:', error);
          results.push({ error: `Batch ${i}-${i + batch.length}: ${error.message}` });
        }
      }

      setResults({
        total: linkData.length,
        successful: results.filter(r => !r.error).length,
        failed: results.filter(r => r.error).length,
        details: results
      });

      queryClient.invalidateQueries({ queryKey: ['/api/affiliate-links'] });

      toast({
        title: 'Bulk Import Complete',
        description: `Processed ${linkData.length} links. ${results.filter(r => !r.error).length} successful.`
      });

    } catch (error: any) {
      toast({
        title: 'Import Failed',
        description: error.message,
        variant: 'destructive'
      });
    } finally {
      setIsProcessing(false);
    }
  };

  const downloadTemplate = () => {
    const template = `url,merchant,product name,category,commission,description,image url
https://amazon.com/dp/B123456,Amazon,Premium Omega-3,supplements,4,High-quality fish oil supplement,https://example.com/image.jpg
https://clickbank.com/product123,ClickBank,Wellness Guide,health,50,Complete wellness transformation guide,https://example.com/guide.jpg`;

    const blob = new Blob([template], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'affiliate-links-template.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Upload className="h-5 w-5" />
            Bulk Import Affiliate Links
          </DialogTitle>
          <DialogDescription>
            Upload a CSV file or paste CSV data to import multiple affiliate links at once
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Template Download */}
          <div className="flex items-center justify-between p-4 border rounded-lg bg-muted/50">
            <div>
              <h3 className="font-medium">Need a template?</h3>
              <p className="text-sm text-muted-foreground">Download our CSV template to get started</p>
            </div>
            <Button variant="outline" onClick={downloadTemplate} className="flex items-center gap-2">
              <Download className="h-4 w-4" />
              Download Template
            </Button>
          </div>

          {/* File Upload */}
          <div className="space-y-2">
            <Label htmlFor="csv-file">Upload CSV File</Label>
            <Input
              id="csv-file"
              type="file"
              accept=".csv,.txt"
              onChange={handleFileUpload}
              disabled={isProcessing}
            />
          </div>

          {/* Manual CSV Input */}
          <div className="space-y-2">
            <Label htmlFor="csv-data">Or Paste CSV Data</Label>
            <Textarea
              id="csv-data"
              value={csvData}
              onChange={(e) => setCsvData(e.target.value)}
              placeholder="url,merchant,product name,category,commission,description,image url&#10;https://amazon.com/dp/B123456,Amazon,Premium Omega-3,supplements,4,High-quality fish oil supplement,https://example.com/image.jpg"
              rows={6}
              disabled={isProcessing}
            />
          </div>

          {/* Progress */}
          {isProcessing && (
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <span className="text-sm font-medium">Processing...</span>
                <span className="text-sm text-muted-foreground">{progress}%</span>
              </div>
              <Progress value={progress} />
            </div>
          )}

          {/* Results */}
          {results && (
            <div className="space-y-3 p-4 border rounded-lg bg-muted/30">
              <h3 className="font-medium flex items-center gap-2">
                <CheckCircle className="h-4 w-4 text-green-600" />
                Import Results
              </h3>
              <div className="grid grid-cols-3 gap-4 text-sm">
                <div>
                  <div className="font-medium">{results.total}</div>
                  <div className="text-muted-foreground">Total Links</div>
                </div>
                <div>
                  <div className="font-medium text-green-600">{results.successful}</div>
                  <div className="text-muted-foreground">Successful</div>
                </div>
                <div>
                  <div className="font-medium text-red-600">{results.failed}</div>
                  <div className="text-muted-foreground">Failed</div>
                </div>
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex gap-2 justify-end">
            <Button variant="outline" onClick={onClose} disabled={isProcessing}>
              {results ? 'Close' : 'Cancel'}
            </Button>
            {!results && (
              <Button 
                onClick={processBulkImport} 
                disabled={isProcessing || (!csvData && !file)}
                className="flex items-center gap-2"
              >
                {isProcessing ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full" />
                    Processing...
                  </>
                ) : (
                  <>
                    <Upload className="h-4 w-4" />
                    Import Links
                  </>
                )}
              </Button>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

