import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Package, 
  Loader2,
  ExternalLink,
  Star,
  ChevronLeft,
  ChevronRight,
  Search,
  DollarSign,
  Tag,
  AlertTriangle
} from "lucide-react";

interface Product {
  id: number;
  name: string;
  price: string;
  description: string | null;
  category: string | null;
  rating: string | null;
  imageUrl: string | null;
  affiliateLink: string | null;
  isRecommended: boolean | null;
  tags?: string[] | null;
  createdAt: string;
  updatedAt: string;
}

interface PaginatedResponse {
  data: Product[];
  pagination: {
    page: number;
    pageSize: number;
    total: number;
    totalPages: number;
  };
}

export function OptimizedProductManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Pagination and filtering state
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(12);
  const [searchTerm, setSearchTerm] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [selectedProducts, setSelectedProducts] = useState<number[]>([]);

  // Form state
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    rating: "",
    imageUrl: "",
    affiliateLink: "",
    isRecommended: false,
    tags: ""
  });

  // Fetch paginated products with caching
  const { data: productData, isLoading, error } = useQuery({
    queryKey: ['/api/admin/products', currentPage, pageSize, searchTerm, categoryFilter],
    queryFn: async () => {
      const params = new URLSearchParams({
        page: currentPage.toString(),
        pageSize: pageSize.toString(),
        ...(searchTerm && { search: searchTerm }),
        ...(categoryFilter && categoryFilter !== 'all' && { category: categoryFilter })
      });

      const response = await apiRequest('GET', `/api/admin/products?${params}`);
      return response as unknown as PaginatedResponse;
    },
    staleTime: 5 * 60 * 1000, // 5 minutes cache
    refetchOnWindowFocus: false
  });

  // Create product mutation
  const createProduct = useMutation({
    mutationFn: (productData: any) => apiRequest('POST', '/api/admin/products', {
      ...productData,
      price: parseFloat(productData.price) || 0,
      rating: parseFloat(productData.rating) || null,
      tags: productData.tags ? productData.tags.split(',').map((t: string) => t.trim()) : []
    }),
    onSuccess: () => {
      toast({ title: "Success", description: "Product created successfully" });
      setIsCreateDialogOpen(false);
      setNewProduct({
        name: "",
        description: "",
        price: "",
        category: "",
        rating: "",
        imageUrl: "",
        affiliateLink: "",
        isRecommended: false,
        tags: ""
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/products'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create product",
        variant: "destructive"
      });
    }
  });

  // Update product mutation
  const updateProduct = useMutation({
    mutationFn: ({ id, data }: { id: number; data: any }) =>
      apiRequest('PUT', `/api/admin/products/${id}`, data),
    onSuccess: () => {
      toast({ title: "Success", description: "Product updated successfully" });
      setEditingProduct(null);
      queryClient.invalidateQueries({ queryKey: ['/api/admin/products'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update product",
        variant: "destructive"
      });
    }
  });

  // Delete product mutation
  const deleteProduct = useMutation({
    mutationFn: (id: number) => apiRequest('DELETE', `/api/admin/products/${id}`),
    onSuccess: () => {
      toast({ title: "Success", description: "Product deleted successfully" });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/products'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete product",
        variant: "destructive"
      });
    }
  });

  // Bulk operations mutation
  const bulkOperation = useMutation({
    mutationFn: ({ action, ids }: { action: string; ids: number[] }) =>
      apiRequest('POST', '/api/admin/products/bulk', { action, ids }),
    onSuccess: (_, variables) => {
      toast({ 
        title: "Success", 
        description: `Bulk ${variables.action} completed for ${variables.ids.length} products` 
      });
      setSelectedProducts([]);
      queryClient.invalidateQueries({ queryKey: ['/api/admin/products'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Bulk operation failed",
        variant: "destructive"
      });
    }
  });

  const handleSelectProduct = (productId: number) => {
    setSelectedProducts(prev => 
      prev.includes(productId) 
        ? prev.filter(id => id !== productId)
        : [...prev, productId]
    );
  };

  const handleSelectAll = () => {
    if (selectedProducts.length === productData?.data?.length) {
      setSelectedProducts([]);
    } else {
      setSelectedProducts(productData?.data?.map(product => product.id) || []);
    }
  };

  const products = productData?.data || [];
  const pagination = productData?.pagination;

  if (error) {
    return (
      <Card>
        <CardContent className="flex items-center justify-center py-10">
          <div className="text-center">
            <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
            <h3 className="text-lg font-semibold">Failed to load products</h3>
            <p className="text-muted-foreground">Please try refreshing the page</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center gap-2">
                <Package className="h-5 w-5" />
                Product Management
                {pagination && (
                  <Badge variant="outline">
                    {pagination.total} total products
                  </Badge>
                )}
              </CardTitle>
              <CardDescription>
                Manage your affiliate products with advanced filtering and bulk operations
              </CardDescription>
            </div>
            <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
              <DialogTrigger asChild>
                <Button>
                  <Plus className="h-4 w-4 mr-2" />
                  Add Product
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>Add New Product</DialogTitle>
                  <DialogDescription>
                    Add a new affiliate product to your platform
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="name">Product Name</Label>
                      <Input
                        id="name"
                        value={newProduct.name}
                        onChange={(e) => setNewProduct(prev => ({ ...prev, name: e.target.value }))}
                        placeholder="Enter product name..."
                      />
                    </div>
                    <div>
                      <Label htmlFor="price">Price</Label>
                      <Input
                        id="price"
                        type="number"
                        step="0.01"
                        value={newProduct.price}
                        onChange={(e) => setNewProduct(prev => ({ ...prev, price: e.target.value }))}
                        placeholder="0.00"
                      />
                    </div>
                  </div>
                  <div>
                    <Label htmlFor="description">Description</Label>
                    <Textarea
                      id="description"
                      value={newProduct.description}
                      onChange={(e) => setNewProduct(prev => ({ ...prev, description: e.target.value }))}
                      placeholder="Product description..."
                      rows={3}
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="category">Category</Label>
                      <Select value={newProduct.category} onValueChange={(value) => setNewProduct(prev => ({ ...prev, category: value }))}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select category" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="supplements">Supplements</SelectItem>
                          <SelectItem value="fitness">Fitness Equipment</SelectItem>
                          <SelectItem value="nutrition">Nutrition</SelectItem>
                          <SelectItem value="wellness">Wellness</SelectItem>
                          <SelectItem value="books">Books</SelectItem>
                          <SelectItem value="courses">Courses</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div>
                      <Label htmlFor="rating">Rating (1-5)</Label>
                      <Input
                        id="rating"
                        type="number"
                        min="1"
                        max="5"
                        step="0.1"
                        value={newProduct.rating}
                        onChange={(e) => setNewProduct(prev => ({ ...prev, rating: e.target.value }))}
                        placeholder="4.5"
                      />
                    </div>
                  </div>
                  <div>
                    <Label htmlFor="imageUrl">Image URL</Label>
                    <Input
                      id="imageUrl"
                      value={newProduct.imageUrl}
                      onChange={(e) => setNewProduct(prev => ({ ...prev, imageUrl: e.target.value }))}
                      placeholder="https://example.com/image.jpg"
                    />
                  </div>
                  <div>
                    <Label htmlFor="affiliateLink">Affiliate Link</Label>
                    <Input
                      id="affiliateLink"
                      value={newProduct.affiliateLink}
                      onChange={(e) => setNewProduct(prev => ({ ...prev, affiliateLink: e.target.value }))}
                      placeholder="https://affiliate-link.com/product"
                    />
                  </div>
                  <div>
                    <Label htmlFor="tags">Tags (comma-separated)</Label>
                    <Input
                      id="tags"
                      value={newProduct.tags}
                      onChange={(e) => setNewProduct(prev => ({ ...prev, tags: e.target.value }))}
                      placeholder="organic, natural, premium"
                    />
                  </div>
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="recommended"
                      checked={newProduct.isRecommended}
                      onCheckedChange={(checked) => setNewProduct(prev => ({ ...prev, isRecommended: !!checked }))}
                    />
                    <Label htmlFor="recommended">Recommended product</Label>
                  </div>
                  <div className="flex justify-end gap-2">
                    <Button variant="outline" onClick={() => setIsCreateDialogOpen(false)}>
                      Cancel
                    </Button>
                    <Button
                      onClick={() => createProduct.mutate(newProduct)}
                      disabled={createProduct.isPending || !newProduct.name || !newProduct.affiliateLink}
                    >
                      {createProduct.isPending && <Loader2 className="h-4 w-4 mr-2 animate-spin" />}
                      Add Product
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
        <CardContent>
          {/* Advanced Filtering */}
          <div className="flex flex-wrap items-center gap-4 mb-6 p-4 bg-muted rounded-lg">
            <div className="flex-1 min-w-[200px]">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search products..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-[180px]">
                <SelectValue placeholder="All categories" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All categories</SelectItem>
                <SelectItem value="supplements">Supplements</SelectItem>
                <SelectItem value="fitness">Fitness Equipment</SelectItem>
                <SelectItem value="nutrition">Nutrition</SelectItem>
                <SelectItem value="wellness">Wellness</SelectItem>
                <SelectItem value="books">Books</SelectItem>
                <SelectItem value="courses">Courses</SelectItem>
              </SelectContent>
            </Select>
            <Select value={pageSize.toString()} onValueChange={(value) => setPageSize(Number(value))}>
              <SelectTrigger className="w-[120px]">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="6">6 per page</SelectItem>
                <SelectItem value="12">12 per page</SelectItem>
                <SelectItem value="24">24 per page</SelectItem>
                <SelectItem value="48">48 per page</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Bulk Operations */}
          {selectedProducts.length > 0 && (
            <div className="flex items-center gap-2 mb-4 p-3 bg-blue-50 rounded-lg">
              <span className="text-sm text-blue-700">
                {selectedProducts.length} product{selectedProducts.length !== 1 ? 's' : ''} selected
              </span>
              <Button
                size="sm"
                variant="outline"
                onClick={() => bulkOperation.mutate({ action: 'recommend', ids: selectedProducts })}
                disabled={bulkOperation.isPending}
              >
                Mark Recommended
              </Button>
              <Button
                size="sm"
                variant="outline"
                onClick={() => bulkOperation.mutate({ action: 'unrecommend', ids: selectedProducts })}
                disabled={bulkOperation.isPending}
              >
                Remove Recommendation
              </Button>
              <Button
                size="sm"
                variant="destructive"
                onClick={() => bulkOperation.mutate({ action: 'delete', ids: selectedProducts })}
                disabled={bulkOperation.isPending}
              >
                Delete
              </Button>
            </div>
          )}

          {/* Products Grid */}
          {isLoading ? (
            <div className="flex items-center justify-center py-10">
              <Loader2 className="h-8 w-8 animate-spin" />
            </div>
          ) : products.length === 0 ? (
            <div className="text-center py-10">
              <Package className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-semibold">No products found</h3>
              <p className="text-muted-foreground">Add your first affiliate product to get started</p>
            </div>
          ) : (
            <>
              <div className="mb-4">
                <div className="flex items-center">
                  <Checkbox
                    checked={selectedProducts.length === products.length && products.length > 0}
                    onCheckedChange={handleSelectAll}
                    className="mr-3"
                  />
                  <span className="text-sm font-medium">Select All</span>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {products.map((product) => (
                  <Card key={product.id} className="group hover:shadow-lg transition-all duration-200">
                    <CardContent className="p-4">
                      <div className="flex items-start gap-3 mb-3">
                        <Checkbox
                          checked={selectedProducts.includes(product.id)}
                          onCheckedChange={() => handleSelectProduct(product.id)}
                        />
                        <div className="flex-1 min-w-0">
                          {product.imageUrl && (
                            <img
                              src={product.imageUrl}
                              alt={product.name}
                              className="w-full h-32 object-cover rounded-lg mb-3"
                              onError={(e) => {
                                e.currentTarget.style.display = 'none';
                              }}
                            />
                          )}
                          <div className="flex items-start justify-between mb-2">
                            <h4 className="font-medium text-sm line-clamp-2">{product.name}</h4>
                            {product.isRecommended && (
                              <Badge variant="secondary" className="ml-2 shrink-0">
                                <Star className="h-3 w-3 mr-1" />
                                Rec
                              </Badge>
                            )}
                          </div>

                          {product.description && (
                            <p className="text-xs text-muted-foreground line-clamp-2 mb-2">
                              {product.description}
                            </p>
                          )}

                          <div className="flex items-center justify-between mb-3">
                            <div className="flex items-center gap-2">
                              {product.price && (
                                <span className="text-sm font-semibold text-green-600 flex items-center">
                                  <DollarSign className="h-3 w-3" />
                                  {product.price}
                                </span>
                              )}
                              {product.rating && (
                                <div className="flex items-center">
                                  <Star className="h-3 w-3 text-yellow-500 fill-current" />
                                  <span className="text-xs ml-1">{product.rating}</span>
                                </div>
                              )}
                            </div>
                            {product.category && (
                              <Badge variant="outline" className="text-xs">
                                {product.category}
                              </Badge>
                            )}
                          </div>

                          <div className="flex items-center gap-2">
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => setEditingProduct(product)}
                              className="flex-1"
                            >
                              <Edit className="h-3 w-3 mr-1" />
                              Edit
                            </Button>
                            {product.affiliateLink && (
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => window.open(product.affiliateLink!, '_blank')}
                              >
                                <ExternalLink className="h-3 w-3" />
                              </Button>
                            )}
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => deleteProduct.mutate(product.id)}
                              disabled={deleteProduct.isPending}
                            >
                              <Trash2 className="h-3 w-3" />
                            </Button>
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </>
          )}

          {/* Pagination */}
          {pagination && pagination.totalPages > 1 && (
            <div className="flex items-center justify-between mt-6">
              <div className="text-sm text-muted-foreground">
                Showing {((pagination.page - 1) * pagination.pageSize) + 1} to{' '}
                {Math.min(pagination.page * pagination.pageSize, pagination.total)} of{' '}
                {pagination.total} products
              </div>
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                  disabled={pagination.page === 1}
                >
                  <ChevronLeft className="h-4 w-4" />
                </Button>
                {Array.from({ length: Math.min(5, pagination.totalPages) }, (_, i) => {
                  const page = i + 1;
                  return (
                    <Button
                      key={page}
                      variant={page === pagination.page ? "default" : "outline"}
                      size="sm"
                      onClick={() => setCurrentPage(page)}
                    >
                      {page}
                    </Button>
                  );
                })}
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setCurrentPage(prev => Math.min(pagination.totalPages, prev + 1))}
                  disabled={pagination.page === pagination.totalPages}
                >
                  <ChevronRight className="h-4 w-4" />
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/admin/ProductManag.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Star, 
  ExternalLink,
  Package,
  DollarSign,
  Tag,
  Loader2
} from "lucide-react";
import { Product } from "@shared/schema";

export default function ProductManagement() {
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    tags: '',
    imageUrl: '',
    affiliateLink: '',
    rating: '',
    isRecommended: false
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: products, isLoading } = useQuery({
    queryKey: ['/api/products'],
  });

  const createProductMutation = useMutation({
    mutationFn: async (product: any) => {
      const response = await apiRequest('POST', '/api/products', {
        ...product,
        price: parseFloat(product.price),
        rating: product.rating ? parseFloat(product.rating) : null,
        tags: product.tags ? product.tags.split(',').map((tag: string) => tag.trim()) : []
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      setIsCreateDialogOpen(false);
      resetForm();
      toast({
        title: "Success",
        description: "Product created successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateProductMutation = useMutation({
    mutationFn: async (product: any) => {
      const response = await apiRequest('PUT', `/api/products/${selectedProduct?.id}`, {
        ...product,
        price: parseFloat(product.price),
        rating: product.rating ? parseFloat(product.rating) : null,
        tags: product.tags ? product.tags.split(',').map((tag: string) => tag.trim()) : []
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      setIsEditDialogOpen(false);
      setSelectedProduct(null);
      resetForm();
      toast({
        title: "Success",
        description: "Product updated successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteProductMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest('DELETE', `/api/products/${id}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      toast({
        title: "Success",
        description: "Product deleted successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setNewProduct({
      name: '',
      description: '',
      price: '',
      category: '',
      tags: '',
      imageUrl: '',
      affiliateLink: '',
      rating: '',
      isRecommended: false
    });
  };

  const handleEdit = (product: Product) => {
    setSelectedProduct(product);
    setNewProduct({
      name: product.name,
      description: product.description || '',
      price: product.price.toString(),
      category: product.category || '',
      tags: product.tags?.join(', ') || '',
      imageUrl: product.imageUrl || '',
      affiliateLink: product.affiliateLink || '',
      rating: product.rating?.toString() || '',
      isRecommended: product.isRecommended || false
    });
    setIsEditDialogOpen(true);
  };

  const handleDelete = (id: number) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      deleteProductMutation.mutate(id);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <Loader2 className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Product Management</h2>
          <p className="text-muted-foreground">Manage affiliate products and recommendations</p>
        </div>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Add Product
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Add New Product</DialogTitle>
              <DialogDescription>
                Add a new wellness product to your recommendations
              </DialogDescription>
            </DialogHeader>
            <ProductForm
              product={newProduct}
              setProduct={setNewProduct}
              onSubmit={() => createProductMutation.mutate(newProduct)}
              isSubmitting={createProductMutation.isPending}
            />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-4">
        {products?.map((product: Product) => (
          <Card key={product.id}>
            <CardHeader>
              <div className="flex justify-between items-start">
                <div className="flex gap-4">
                  {product.imageUrl && (
                    <img
                      src={product.imageUrl}
                      alt={product.name}
                      className="w-16 h-16 object-cover rounded-lg"
                    />
                  )}
                  <div>
                    <CardTitle className="text-lg">{product.name}</CardTitle>
                    <CardDescription className="mt-1">
                      {product.description?.substring(0, 100)}...
                    </CardDescription>
                    <div className="flex items-center gap-2 mt-2">
                      <Badge variant="outline">${product.price}</Badge>
                      {product.rating && (
                        <Badge variant="outline" className="flex items-center gap-1">
                          <Star className="h-3 w-3 fill-current" />
                          {product.rating}
                        </Badge>
                      )}
                    </div>
                  </div>
                </div>
                <div className="flex gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleEdit(product)}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleDelete(product.id)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="flex flex-wrap gap-2 mb-4">
                {product.isRecommended && (
                  <Badge className="bg-green-100 text-green-800">
                    <Star className="h-3 w-3 mr-1" />
                    Recommended
                  </Badge>
                )}
                {product.category && (
                  <Badge variant="outline">{product.category}</Badge>
                )}
                {product.tags?.map((tag) => (
                  <Badge key={tag} variant="secondary">{tag}</Badge>
                ))}
              </div>
              {product.affiliateLink && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => window.open(product.affiliateLink, '_blank')}
                >
                  <ExternalLink className="h-4 w-4 mr-2" />
                  View Product
                </Button>
              )}
            </CardContent>
          </Card>
        ))}
      </div>

      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit Product</DialogTitle>
            <DialogDescription>
              Update the product details
            </DialogDescription>
          </DialogHeader>
          <ProductForm
            product={newProduct}
            setProduct={setNewProduct}
            onSubmit={() => updateProductMutation.mutate(newProduct)}
            isSubmitting={updateProductMutation.isPending}
            isEditing={true}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

function ProductForm({ 
  product, 
  setProduct, 
  onSubmit, 
  isSubmitting, 
  isEditing = false 
}: {
  product: any;
  setProduct: (product: any) => void;
  onSubmit: () => void;
  isSubmitting: boolean;
  isEditing?: boolean;
}) {
  return (
    <div className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="name">Product Name</Label>
          <Input
            id="name"
            value={product.name}
            onChange={(e) => setProduct({ ...product, name: e.target.value })}
            placeholder="Enter product name"
          />
        </div>
        <div>
          <Label htmlFor="price">Price</Label>
          <Input
            id="price"
            type="number"
            step="0.01"
            value={product.price}
            onChange={(e) => setProduct({ ...product, price: e.target.value })}
            placeholder="0.00"
          />
        </div>
      </div>

      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={product.description}
          onChange={(e) => setProduct({ ...product, description: e.target.value })}
          placeholder="Product description"
          rows={4}
        />
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="category">Category</Label>
          <Select value={product.category} onValueChange={(value) => setProduct({ ...product, category: value })}>
            <SelectTrigger>
              <SelectValue placeholder="Select category" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="supplements">Supplements</SelectItem>
              <SelectItem value="fitness">Fitness Equipment</SelectItem>
              <SelectItem value="skincare">Skincare</SelectItem>
              <SelectItem value="nutrition">Nutrition</SelectItem>
              <SelectItem value="wellness">Wellness</SelectItem>
              <SelectItem value="books">Books</SelectItem>
            </SelectContent>
          </Select>
        </div>
        <div>
          <Label htmlFor="rating">Rating (1-5)</Label>
          <Input
            id="rating"
            type="number"
            min="1"
            max="5"
            step="0.1"
            value={product.rating}
            onChange={(e) => setProduct({ ...product, rating: e.target.value })}
            placeholder="4.5"
          />
        </div>
      </div>

      <div>
        <Label htmlFor="tags">Tags (comma-separated)</Label>
        <Input
          id="tags"
          value={product.tags}
          onChange={(e) => setProduct({ ...product, tags: e.target.value })}
          placeholder="organic, natural, vegan"
        />
      </div>

      <div>
        <Label htmlFor="imageUrl">Image URL</Label>
        <Input
          id="imageUrl"
          value={product.imageUrl}
          onChange={(e) => setProduct({ ...product, imageUrl: e.target.value })}
          placeholder="https://example.com/image.jpg"
        />
      </div>

      <div>
        <Label htmlFor="affiliateLink">Affiliate Link</Label>
        <Input
          id="affiliateLink"
          value={product.affiliateLink}
          onChange={(e) => setProduct({ ...product, affiliateLink: e.target.value })}
          placeholder="https://affiliate-link.com"
        />
      </div>

      <div className="flex items-center space-x-2">
        <Switch
          id="isRecommended"
          checked={product.isRecommended}
          onCheckedChange={(checked) => setProduct({ ...product, isRecommended: checked })}
        />
        <Label htmlFor="isRecommended">Recommended Product</Label>
      </div>

      <Button onClick={onSubmit} disabled={isSubmitting}>
        {isSubmitting && <Loader2 className="h-4 w-4 mr-2 animate-spin" />}
        {isEditing ? 'Update Product' : 'Create Product'}
      </Button>
    </div>
  );
}

client/src/components/admin/SearchAndFil.content:
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Search, Filter, X } from 'lucide-react';
import type { AdminFilters } from '@shared/types/admin';

interface SearchAndFiltersProps {
  filters: AdminFilters;
  onFilterChange: (key: keyof AdminFilters, value: any) => void;
  onResetFilters: () => void;
  categories?: { value: string; label: string; }[];
  totalCount: number;
  filteredCount: number;
  className?: string;
}

export function SearchAndFilters({
  filters,
  onFilterChange,
  onResetFilters,
  categories = [],
  totalCount,
  filteredCount,
  className = ""
}: SearchAndFiltersProps) {
  const hasActiveFilters = filters.searchTerm || 
    filters.categoryFilter !== 'all' || 
    filters.statusFilter !== 'all';

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Search and Filter Controls */}
      <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
          <Input
            placeholder="Search content..."
            value={filters.searchTerm}
            onChange={(e) => onFilterChange('searchTerm', e.target.value)}
            className="pl-10"
          />
        </div>

        <div className="flex gap-2 items-center">
          {categories.length > 0 && (
            <Select 
              value={filters.categoryFilter} 
              onValueChange={(value) => onFilterChange('categoryFilter', value)}
            >
              <SelectTrigger className="w-40">
                <Filter className="h-4 w-4 mr-2" />
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Categories</SelectItem>
                {categories.map(category => (
                  <SelectItem key={category.value} value={category.value}>
                    {category.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          )}

          <Select 
            value={filters.statusFilter} 
            onValueChange={(value) => onFilterChange('statusFilter', value)}
          >
            <SelectTrigger className="w-32">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="published">Published</SelectItem>
              <SelectItem value="draft">Draft</SelectItem>
            </SelectContent>
          </Select>

          {hasActiveFilters && (
            <Button 
              variant="outline" 
              size="sm" 
              onClick={onResetFilters}
              className="flex items-center gap-2"
            >
              <X className="h-4 w-4" />
              Clear
            </Button>
          )}
        </div>
      </div>

      {/* Filter Summary */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2 text-sm text-muted-foreground">
          <span>
            Showing {filteredCount} of {totalCount} items
          </span>

          {hasActiveFilters && (
            <div className="flex items-center gap-2">
              <span>‚Ä¢</span>
              <div className="flex gap-1">
                {filters.searchTerm && (
                  <Badge variant="secondary" className="text-xs">
                    Search: "{filters.searchTerm}"
                  </Badge>
                )}
                {filters.categoryFilter !== 'all' && (
                  <Badge variant="secondary" className="text-xs">
                    Category: {filters.categoryFilter}
                  </Badge>
                )}
                {filters.statusFilter !== 'all' && (
                  <Badge variant="secondary" className="text-xs">
                    Status: {filters.statusFilter}
                  </Badge>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/admin/SmartFormEnh.content:
import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { 
  CheckCircle, 
  AlertCircle, 
  Loader2, 
  Lightbulb,
  TrendingUp,
  DollarSign,
  Star
} from 'lucide-react';

interface SmartFormEnhancementsProps {
  url: string;
  onUrlChange: (url: string) => void;
  onSuggestionSelect: (suggestion: any) => void;
  disabled?: boolean;
}

export function SmartFormEnhancements({ 
  url, 
  onUrlChange, 
  onSuggestionSelect,
  disabled 
}: SmartFormEnhancementsProps) {
  const [urlValidation, setUrlValidation] = useState<{
    isValid: boolean;
    status: 'idle' | 'validating' | 'valid' | 'invalid';
    message?: string;
  }>({ isValid: false, status: 'idle' });

  const [suggestions, setSuggestions] = useState<any[]>([]);
  const { toast } = useToast();

  // Real-time URL validation
  useEffect(() => {
    if (!url || url.length < 10) {
      setUrlValidation({ isValid: false, status: 'idle' });
      return;
    }

    const validateUrl = async () => {
      setUrlValidation({ isValid: false, status: 'validating' });

      try {
        // Basic URL validation
        const urlPattern = /^https?:\/\/.+/i;
        if (!urlPattern.test(url)) {
          setUrlValidation({
            isValid: false,
            status: 'invalid',
            message: 'Please enter a valid HTTP/HTTPS URL'
          });
          return;
        }

        // Check if it's a supported merchant
        const supportedMerchants = [
          { domain: 'amazon.com', name: 'Amazon', commission: '4-8%' },
          { domain: 'clickbank.com', name: 'ClickBank', commission: '10-75%' },
          { domain: 'shareasale.com', name: 'ShareASale', commission: '5-20%' },
          { domain: 'commissionjunction.com', name: 'CJ Affiliate', commission: '3-15%' },
          { domain: 'impact.com', name: 'Impact', commission: '5-25%' }
        ];

        const merchant = supportedMerchants.find(m => url.includes(m.domain));

        if (merchant) {
          setUrlValidation({
            isValid: true,
            status: 'valid',
            message: `${merchant.name} - Typical commission: ${merchant.commission}`
          });

          // Generate intelligent suggestions
          await generateSuggestions(url, merchant);
        } else {
          setUrlValidation({
            isValid: true,
            status: 'valid',
            message: 'URL appears valid, but merchant not recognized'
          });
        }

      } catch (error) {
        setUrlValidation({
          isValid: false,
          status: 'invalid',
          message: 'Invalid URL format'
        });
      }
    };

    const debounceTimer = setTimeout(validateUrl, 500);
    return () => clearTimeout(debounceTimer);
  }, [url]);

  const generateSuggestions = async (productUrl: string, merchant: any) => {
    try {
      // Generate AI-powered suggestions based on URL analysis
      const suggestions = [
        {
          type: 'category',
          title: 'Suggested Category',
          value: inferCategory(productUrl),
          confidence: 85,
          icon: 'üè∑Ô∏è'
        },
        {
          type: 'commission',
          title: 'Estimated Commission',
          value: merchant.commission.split('-')[1] || '5%',
          confidence: 70,
          icon: 'üí∞'
        },
        {
          type: 'keywords',
          title: 'SEO Keywords',
          value: extractKeywords(productUrl),
          confidence: 90,
          icon: 'üîç'
        },
        {
          type: 'competition',
          title: 'Competition Level',
          value: 'Medium',
          confidence: 60,
          icon: 'üìä'
        }
      ];

      setSuggestions(suggestions);
    } catch (error) {
      console.error('Error generating suggestions:', error);
    }
  };

  const inferCategory = (url: string): string => {
    const categoryKeywords = {
      'supplements': ['vitamin', 'supplement', 'protein', 'omega', 'probiotic'],
      'fitness': ['fitness', 'exercise', 'workout', 'gym', 'weight'],
      'beauty': ['beauty', 'skincare', 'cosmetic', 'face', 'serum'],
      'health': ['health', 'wellness', 'medical', 'therapy', 'care'],
      'nutrition': ['nutrition', 'diet', 'organic', 'natural', 'food']
    };

    const lowerUrl = url.toLowerCase();

    for (const [category, keywords] of Object.entries(categoryKeywords)) {
      if (keywords.some(keyword => lowerUrl.includes(keyword))) {
        return category;
      }
    }

    return 'general';
  };

  const extractKeywords = (url: string): string => {
    // Extract potential keywords from URL structure
    const urlParts = url.split('/').join(' ').split('-').join(' ').split('_').join(' ');
    const keywords = urlParts.match(/[a-zA-Z]+/g) || [];

    return keywords
      .filter(word => word.length > 3)
      .slice(0, 3)
      .join(', ');
  };

  const handleSuggestionClick = (suggestion: any) => {
    onSuggestionSelect(suggestion);
    toast({
      title: 'Suggestion Applied',
      description: `Applied ${suggestion.title}: ${suggestion.value}`
    });
  };

  const getValidationIcon = () => {
    switch (urlValidation.status) {
      case 'validating':
        return <Loader2 className="h-4 w-4 animate-spin text-blue-500" />;
      case 'valid':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'invalid':
        return <AlertCircle className="h-4 w-4 text-red-500" />;
      default:
        return null;
    }
  };

  return (
    <div className="space-y-4">
      {/* Enhanced URL Input */}
      <div className="relative">
        <Input
          value={url}
          onChange={(e) => onUrlChange(e.target.value)}
          placeholder="https://amazon.com/dp/B12345 (paste any product URL)"
          disabled={disabled}
          className="pr-10"
        />
        <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
          {getValidationIcon()}
        </div>
      </div>

      {/* Validation Message */}
      {urlValidation.message && (
        <div className={`text-sm flex items-center gap-2 ${
          urlValidation.status === 'valid' ? 'text-green-600' : 'text-red-600'
        }`}>
          {urlValidation.message}
        </div>
      )}

      {/* AI-Powered Suggestions */}
      {suggestions.length > 0 && (
        <Card className="p-4 bg-gradient-to-r from-blue-50 to-purple-50 border-blue-200">
          <div className="flex items-center gap-2 mb-3">
            <Lightbulb className="h-4 w-4 text-blue-600" />
            <span className="font-medium text-blue-900">Smart Suggestions</span>
          </div>

          <div className="grid grid-cols-2 gap-3">
            {suggestions.map((suggestion, index) => (
              <div
                key={index}
                className="p-3 bg-white rounded-lg border border-blue-100 hover:border-blue-300 cursor-pointer transition-colors"
                onClick={() => handleSuggestionClick(suggestion)}
              >
                <div className="flex items-center justify-between mb-1">
                  <span className="text-sm font-medium flex items-center gap-1">
                    <span>{suggestion.icon}</span>
                    {suggestion.title}
                  </span>
                  <Badge variant="secondary" className="text-xs">
                    {suggestion.confidence}%
                  </Badge>
                </div>
                <div className="text-sm text-muted-foreground">
                  {suggestion.value}
                </div>
              </div>
            ))}
          </div>
        </Card>
      )}

      {/* Quick Templates */}
      <div className="grid grid-cols-3 gap-2">
        {[
          { name: 'Amazon Product', pattern: 'https://amazon.com/dp/', category: 'supplements' },
          { name: 'ClickBank Offer', pattern: 'https://clickbank.com/', category: 'health' },
          { name: 'ShareASale Link', pattern: 'https://shareasale.com/', category: 'fitness' }
        ].map((template, index) => (
          <Button
            key={index}
            variant="outline"
            size="sm"
            onClick={() => {
              onUrlChange(template.pattern + 'YOUR_ID_HERE');
              onSuggestionSelect({ type: 'category', value: template.category });
            }}
            className="text-xs"
          >
            {template.name}
          </Button>
        ))}
      </div>
    </div>
  );
}

client/src/components/agent/AgentCard.ts.content:
// Agent Card Component - Real-time agent status display
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { Play, Pause, RotateCcw, AlertTriangle, CheckCircle, Clock } from 'lucide-react';
import { cardClasses } from '@/theme';

export interface AgentStatus {
  id: string;
  name: string;
  role: 'content-creator' | 'affiliate-scraper' | 'social-poster' | 'wellness-coach' | 'automation-controller';
  status: 'idle' | 'active' | 'failed' | 'completed' | 'paused';
  progress?: number;
  lastExecuted?: Date;
  tasksCompleted: number;
  nextExecution?: Date;
  errorMessage?: string;
  uptime: number; // percentage
}

interface AgentCardProps {
  agent: AgentStatus;
  onStart: (agentId: string) => void;
  onPause: (agentId: string) => void;
  onRestart: (agentId: string) => void;
  className?: string;
}

const statusConfig = {
  idle: {
    color: 'bg-gray-100 text-gray-700',
    icon: Clock,
    badgeVariant: 'secondary' as const,
  },
  active: {
    color: 'bg-green-100 text-green-700',
    icon: CheckCircle,
    badgeVariant: 'default' as const,
  },
  failed: {
    color: 'bg-red-100 text-red-700',
    icon: AlertTriangle,
    badgeVariant: 'destructive' as const,
  },
  completed: {
    color: 'bg-blue-100 text-blue-700',
    icon: CheckCircle,
    badgeVariant: 'default' as const,
  },
  paused: {
    color: 'bg-yellow-100 text-yellow-700',
    icon: Pause,
    badgeVariant: 'secondary' as const,
  },
};

const roleDisplayNames = {
  'content-creator': 'Content Creator',
  'affiliate-scraper': 'Affiliate Scraper',
  'social-poster': 'Social Media Poster',
  'wellness-coach': 'AI Wellness Coach',
  'automation-controller': 'Automation Controller',
};

export const AgentCard = ({ agent, onStart, onPause, onRestart, className }: AgentCardProps) => {
  const config = statusConfig[agent.status];
  const StatusIcon = config.icon;

  const formatLastExecuted = (date?: Date) => {
    if (!date) return 'Never';
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const minutes = Math.floor(diff / (1000 * 60));
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) return `${days}d ago`;
    if (hours > 0) return `${hours}h ago`;
    if (minutes > 0) return `${minutes}m ago`;
    return 'Just now';
  };

  const formatNextExecution = (date?: Date) => {
    if (!date) return 'Not scheduled';
    return new Intl.DateTimeFormat('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      day: 'numeric',
      month: 'short',
    }).format(date);
  };

  return (
    <TooltipProvider>
      <Card className={`${cardClasses} ${className}`}>
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <div className={`p-2 rounded-lg ${config.color}`}>
                <StatusIcon className="h-4 w-4" />
              </div>
              <div>
                <CardTitle className="text-lg font-semibold">{agent.name}</CardTitle>
                <p className="text-sm text-gray-600">{roleDisplayNames[agent.role]}</p>
              </div>
            </div>
            <Badge variant={config.badgeVariant}>{agent.status}</Badge>
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          {/* Progress Bar for Active Agents */}
          {agent.status === 'active' && agent.progress !== undefined && (
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Progress</span>
                <span>{agent.progress}%</span>
              </div>
              <Progress value={agent.progress} className="h-2" />
            </div>
          )}

          {/* Uptime & Performance Metrics */}
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <p className="text-gray-600">Uptime</p>
              <p className="font-semibold">{agent.uptime}%</p>
            </div>
            <div>
              <p className="text-gray-600">Tasks Completed</p>
              <p className="font-semibold">{agent.tasksCompleted.toLocaleString()}</p>
            </div>
          </div>

          {/* Execution Timing */}
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <p className="text-gray-600">Last Run</p>
              <p className="font-medium">{formatLastExecuted(agent.lastExecuted)}</p>
            </div>
            <div>
              <p className="text-gray-600">Next Run</p>
              <p className="font-medium">{formatNextExecution(agent.nextExecution)}</p>
            </div>
          </div>

          {/* Error Message */}
          {agent.status === 'failed' && agent.errorMessage && (
            <div className="p-3 bg-red-50 border border-red-200 rounded-lg">
              <p className="text-sm text-red-700 font-medium">Error:</p>
              <p className="text-sm text-red-600">{agent.errorMessage}</p>
            </div>
          )}
        </CardContent>

        <CardFooter className="pt-3 border-t">
          <div className="flex space-x-2 w-full">
            {agent.status === 'idle' || agent.status === 'paused' ? (
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    size="sm"
                    onClick={() => onStart(agent.id)}
                    className="flex-1"
                  >
                    <Play className="h-4 w-4 mr-1" />
                    Start
                  </Button>
                </TooltipTrigger>
                <TooltipContent>Start agent execution</TooltipContent>
              </Tooltip>
            ) : agent.status === 'active' ? (
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    size="sm"
                    variant="secondary"
                    onClick={() => onPause(agent.id)}
                    className="flex-1"
                  >
                    <Pause className="h-4 w-4 mr-1" />
                    Pause
                  </Button>
                </TooltipTrigger>
                <TooltipContent>Pause agent execution</TooltipContent>
              </Tooltip>
            ) : null}

            {(agent.status === 'failed' || agent.status === 'completed') && (
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => onRestart(agent.id)}
                    className="flex-1"
                  >
                    <RotateCcw className="h-4 w-4 mr-1" />
                    Restart
                  </Button>
                </TooltipTrigger>
                <TooltipContent>Restart agent</TooltipContent>
              </Tooltip>
            )}
          </div>
        </CardFooter>
      </Card>
    </TooltipProvider>
  );
};

export default AgentCard;

client/src/components/agent/AgentHealthB.content:
// Agent Health Bar - System status and API health monitoring
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { 
  Activity, 
  Wifi, 
  Database, 
  Brain, 
  Zap, 
  AlertTriangle, 
  CheckCircle,
  Clock,
  TrendingUp
} from 'lucide-react';

export interface ServiceStatus {
  name: string;
  status: 'healthy' | 'degraded' | 'down' | 'maintenance';
  uptime: number; // percentage
  responseTime: number; // in ms
  lastChecked: Date;
  errorCount: number;
}

export interface SystemHealth {
  overall: 'healthy' | 'degraded' | 'critical';
  uptime: number;
  totalRequests: number;
  successRate: number;
  services: ServiceStatus[];
  performance: {
    avgResponseTime: number;
    peakResponseTime: number;
    throughput: number; // requests per minute
  };
}

interface AgentHealthBarProps {
  systemHealth: SystemHealth;
  className?: string;
}

const statusConfig = {
  healthy: {
    color: 'bg-green-100 text-green-700 border-green-200',
    badgeVariant: 'default' as const,
    icon: CheckCircle,
  },
  degraded: {
    color: 'bg-yellow-100 text-yellow-700 border-yellow-200',
    badgeVariant: 'secondary' as const,
    icon: AlertTriangle,
  },
  down: {
    color: 'bg-red-100 text-red-700 border-red-200',
    badgeVariant: 'destructive' as const,
    icon: AlertTriangle,
  },
  critical: {
    color: 'bg-red-100 text-red-700 border-red-200',
    badgeVariant: 'destructive' as const,
    icon: AlertTriangle,
  },
  maintenance: {
    color: 'bg-blue-100 text-blue-700 border-blue-200',
    badgeVariant: 'secondary' as const,
    icon: Clock,
  },
};

const serviceIcons = {
  'Database': Database,
  'OpenAI API': Brain,
  'DeepSeek API': Brain,
  'Authentication': Wifi,
  'Automation Controller': Zap,
  'Content Pipeline': Activity,
};

export const AgentHealthBar = ({ systemHealth, className }: AgentHealthBarProps) => {
  const overallConfig = statusConfig[systemHealth.overall];
  const OverallIcon = overallConfig.icon;

  const formatUptime = (uptime: number) => {
    return `${uptime.toFixed(2)}%`;
  };

  const formatResponseTime = (time: number) => {
    if (time < 1000) return `${time}ms`;
    return `${(time / 1000).toFixed(1)}s`;
  };

  const formatTimeAgo = (date: Date) => {
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const seconds = Math.floor(diff / 1000);
    const minutes = Math.floor(seconds / 60);

    if (minutes > 0) return `${minutes}m ago`;
    return `${seconds}s ago`;
  };

  const getUptimeColor = (uptime: number) => {
    if (uptime >= 99.5) return 'text-green-600';
    if (uptime >= 95) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getResponseTimeColor = (time: number) => {
    if (time < 200) return 'text-green-600';
    if (time < 500) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <TooltipProvider>
      <Card className={className}>
        <CardHeader className="pb-3">
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Activity className="h-5 w-5 text-primary-500" />
              <span>System Health</span>
            </div>
            <Badge variant={overallConfig.badgeVariant} className="flex items-center space-x-1">
              <OverallIcon className="h-3 w-3" />
              <span className="capitalize">{systemHealth.overall}</span>
            </Badge>
          </CardTitle>
        </CardHeader>

        <CardContent className="space-y-6">
          {/* Overall System Metrics */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-green-600">
                {formatUptime(systemHealth.uptime)}
              </p>
              <p className="text-sm text-gray-600">Uptime</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-blue-600">
                {systemHealth.totalRequests.toLocaleString()}
              </p>
              <p className="text-sm text-gray-600">Total Requests</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-purple-600">
                {systemHealth.successRate.toFixed(1)}%
              </p>
              <p className="text-sm text-gray-600">Success Rate</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-orange-600">
                {systemHealth.performance.throughput}
              </p>
              <p className="text-sm text-gray-600">Req/min</p>
            </div>
          </div>

          {/* Performance Metrics */}
          <div className="space-y-3">
            <h4 className="font-medium flex items-center space-x-2">
              <TrendingUp className="h-4 w-4" />
              <span>Performance</span>
            </h4>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              <div>
                <p className="text-gray-600">Avg Response Time</p>
                <p className={`font-semibold ${getResponseTimeColor(systemHealth.performance.avgResponseTime)}`}>
                  {formatResponseTime(systemHealth.performance.avgResponseTime)}
                </p>
              </div>
              <div>
                <p className="text-gray-600">Peak Response Time</p>
                <p className={`font-semibold ${getResponseTimeColor(systemHealth.performance.peakResponseTime)}`}>
                  {formatResponseTime(systemHealth.performance.peakResponseTime)}
                </p>
              </div>
              <div>
                <p className="text-gray-600">Throughput</p>
                <p className="font-semibold text-blue-600">
                  {systemHealth.performance.throughput} req/min
                </p>
              </div>
            </div>
          </div>

          {/* Individual Service Status */}
          <div className="space-y-3">
            <h4 className="font-medium">Service Status</h4>
            <div className="space-y-2">
              {systemHealth.services.map((service, index) => {
                const config = statusConfig[service.status];
                const ServiceIcon = serviceIcons[service.name as keyof typeof serviceIcons] || Activity;

                return (
                  <Tooltip key={index}>
                    <TooltipTrigger asChild>
                      <div className={`p-3 border rounded-lg ${config.color} hover:opacity-80 cursor-pointer`}>
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-3">
                            <ServiceIcon className="h-4 w-4" />
                            <div>
                              <p className="font-medium">{service.name}</p>
                              <p className="text-xs opacity-75">
                                {formatResponseTime(service.responseTime)} ‚Ä¢ {formatUptime(service.uptime)}
                              </p>
                            </div>
                          </div>
                          <div className="text-right">
                            <Badge variant={config.badgeVariant} className="text-xs mb-1">
                              {service.status}
                            </Badge>
                            <p className="text-xs opacity-75">
                              {formatTimeAgo(service.lastChecked)}
                            </p>
                          </div>
                        </div>

                        {/* Uptime Progress Bar */}
                        <div className="mt-2">
                          <Progress 
                            value={service.uptime} 
                            className="h-1 bg-white/30"
                          />
                        </div>
                      </div>
                    </TooltipTrigger>
                    <TooltipContent side="bottom" className="max-w-xs">
                      <div className="space-y-2">
                        <p className="font-medium">{service.name} Details</p>
                        <div className="text-sm space-y-1">
                          <p>Status: <span className="capitalize">{service.status}</span></p>
                          <p>Uptime: {formatUptime(service.uptime)}</p>
                          <p>Response Time: {formatResponseTime(service.responseTime)}</p>
                          <p>Error Count: {service.errorCount}</p>
                          <p>Last Checked: {formatTimeAgo(service.lastChecked)}</p>
                        </div>
                      </div>
                    </TooltipContent>
                  </Tooltip>
                );
              })}
            </div>
          </div>

          {/* System Alerts */}
          {systemHealth.overall !== 'healthy' && (
            <div className="p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
              <div className="flex items-start space-x-2">
                <AlertTriangle className="h-5 w-5 text-yellow-600 mt-0.5" />
                <div>
                  <p className="font-medium text-yellow-800">System Alert</p>
                  <p className="text-sm text-yellow-700">
                    {systemHealth.overall === 'degraded' 
                      ? 'Some services are experiencing performance issues.'
                      : 'Critical system issues detected. Please check service status.'
                    }
                  </p>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </TooltipProvider>
  );
};

export default AgentHealthBar;

client/src/components/agent/AgentHistory.content:
// Agent History View - Task execution history and memory logs
import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { 
  Clock, 
  CheckCircle, 
  AlertTriangle, 
  Brain, 
  Search, 
  Filter,
  Download,
  RefreshCw,
  Trash2
} from 'lucide-react';

export interface TaskHistory {
  id: string;
  title: string;
  agentType: string;
  status: 'completed' | 'failed' | 'running' | 'queued';
  startTime: Date;
  endTime?: Date;
  duration?: number; // in seconds
  output?: string;
  errorMessage?: string;
  memoryContext?: string[];
  parameters?: Record<string, any>;
}

export interface MemoryLog {
  id: string;
  timestamp: Date;
  type: 'task' | 'learning' | 'error' | 'optimization';
  content: string;
  agentId: string;
  context?: Record<string, any>;
  importance: 'low' | 'medium' | 'high';
}

interface AgentHistoryViewProps {
  taskHistory: TaskHistory[];
  memoryLogs: MemoryLog[];
  onClearHistory: () => void;
  onExportLogs: () => void;
  onRefresh: () => void;
  className?: string;
}

const statusConfig = {
  completed: { color: 'bg-green-100 text-green-700', icon: CheckCircle },
  failed: { color: 'bg-red-100 text-red-700', icon: AlertTriangle },
  running: { color: 'bg-blue-100 text-blue-700', icon: RefreshCw },
  queued: { color: 'bg-gray-100 text-gray-700', icon: Clock },
};

const memoryTypeConfig = {
  task: { color: 'bg-blue-100 text-blue-700', label: 'Task' },
  learning: { color: 'bg-green-100 text-green-700', label: 'Learning' },
  error: { color: 'bg-red-100 text-red-700', label: 'Error' },
  optimization: { color: 'bg-purple-100 text-purple-700', label: 'Optimization' },
};

export const AgentHistoryView = ({ 
  taskHistory, 
  memoryLogs, 
  onClearHistory, 
  onExportLogs, 
  onRefresh,
  className 
}: AgentHistoryViewProps) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [memoryTypeFilter, setMemoryTypeFilter] = useState<string>('all');

  const formatDuration = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) return `${hours}h ${minutes}m ${secs}s`;
    if (minutes > 0) return `${minutes}m ${secs}s`;
    return `${secs}s`;
  };

  const formatTimeAgo = (date: Date) => {
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const minutes = Math.floor(diff / (1000 * 60));
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) return `${days}d ago`;
    if (hours > 0) return `${hours}h ago`;
    if (minutes > 0) return `${minutes}m ago`;
    return 'Just now';
  };

  const filteredTaskHistory = taskHistory.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         task.agentType.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || task.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const filteredMemoryLogs = memoryLogs.filter(log => {
    const matchesSearch = log.content.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = memoryTypeFilter === 'all' || log.type === memoryTypeFilter;
    return matchesSearch && matchesType;
  });

  return (
    <Card className={className}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center space-x-2">
            <Brain className="h-5 w-5 text-primary-500" />
            <span>Agent History & Memory</span>
          </CardTitle>
          <div className="flex space-x-2">
            <Button variant="outline" size="sm" onClick={onRefresh}>
              <RefreshCw className="h-4 w-4 mr-1" />
              Refresh
            </Button>
            <Button variant="outline" size="sm" onClick={onExportLogs}>
              <Download className="h-4 w-4 mr-1" />
              Export
            </Button>
            <Button variant="outline" size="sm" onClick={onClearHistory}>
              <Trash2 className="h-4 w-4 mr-1" />
              Clear
            </Button>
          </div>
        </div>
      </CardHeader>

      <CardContent>
        {/* Search and Filters */}
        <div className="flex space-x-4 mb-6">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <Input
                placeholder="Search tasks and logs..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>
          </div>
        </div>

        <Tabs defaultValue="tasks" className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="tasks">Task History ({taskHistory.length})</TabsTrigger>
            <TabsTrigger value="memory">Memory Logs ({memoryLogs.length})</TabsTrigger>
          </TabsList>

          <TabsContent value="tasks" className="space-y-4">
            {/* Task Status Filter */}
            <div className="flex items-center space-x-2">
              <Filter className="h-4 w-4 text-gray-500" />
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-[160px]">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                  <SelectItem value="failed">Failed</SelectItem>
                  <SelectItem value="running">Running</SelectItem>
                  <SelectItem value="queued">Queued</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <ScrollArea className="h-[400px] w-full">
              <div className="space-y-3">
                {filteredTaskHistory.map((task) => {
                  const config = statusConfig[task.status];
                  const StatusIcon = config.icon;

                  return (
                    <div key={task.id} className="p-4 border rounded-lg hover:bg-gray-50">
                      <div className="flex items-start justify-between mb-2">
                        <div className="flex items-center space-x-3">
                          <div className={`p-2 rounded-lg ${config.color}`}>
                            <StatusIcon className="h-4 w-4" />
                          </div>
                          <div>
                            <h4 className="font-medium">{task.title}</h4>
                            <p className="text-sm text-gray-600">{task.agentType}</p>
                          </div>
                        </div>
                        <div className="text-right text-sm text-gray-500">
                          <p>{formatTimeAgo(task.startTime)}</p>
                          {task.duration && (
                            <p>{formatDuration(task.duration)}</p>
                          )}
                        </div>
                      </div>

                      {task.output && (
                        <div className="mt-3 p-3 bg-gray-50 rounded text-sm">
                          <p className="font-medium mb-1">Output:</p>
                          <p className="text-gray-700">{task.output}</p>
                        </div>
                      )}

                      {task.errorMessage && (
                        <div className="mt-3 p-3 bg-red-50 border border-red-200 rounded text-sm">
                          <p className="font-medium mb-1 text-red-700">Error:</p>
                          <p className="text-red-600">{task.errorMessage}</p>
                        </div>
                      )}

                      {task.memoryContext && task.memoryContext.length > 0 && (
                        <div className="mt-3">
                          <p className="text-sm font-medium mb-2">Memory Context:</p>
                          <div className="flex flex-wrap gap-1">
                            {task.memoryContext.map((context, index) => (
                              <Badge key={index} variant="outline" className="text-xs">
                                {context}
                              </Badge>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  );
                })}

                {filteredTaskHistory.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    <Clock className="h-8 w-8 mx-auto mb-2 opacity-50" />
                    <p>No task history found</p>
                  </div>
                )}
              </div>
            </ScrollArea>
          </TabsContent>

          <TabsContent value="memory" className="space-y-4">
            {/* Memory Type Filter */}
            <div className="flex items-center space-x-2">
              <Filter className="h-4 w-4 text-gray-500" />
              <Select value={memoryTypeFilter} onValueChange={setMemoryTypeFilter}>
                <SelectTrigger className="w-[160px]">
                  <SelectValue placeholder="Filter by type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="task">Task</SelectItem>
                  <SelectItem value="learning">Learning</SelectItem>
                  <SelectItem value="error">Error</SelectItem>
                  <SelectItem value="optimization">Optimization</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <ScrollArea className="h-[400px] w-full">
              <div className="space-y-3">
                {filteredMemoryLogs.map((log) => {
                  const typeConfig = memoryTypeConfig[log.type];

                  return (
                    <div key={log.id} className="p-4 border rounded-lg hover:bg-gray-50">
                      <div className="flex items-start justify-between mb-2">
                        <div className="flex items-center space-x-2">
                          <Badge className={typeConfig.color}>{typeConfig.label}</Badge>
                          {log.importance === 'high' && (
                            <Badge variant="destructive" className="text-xs">High Priority</Badge>
                          )}
                        </div>
                        <span className="text-xs text-gray-500">
                          {formatTimeAgo(log.timestamp)}
                        </span>
                      </div>

                      <p className="text-sm text-gray-700 mb-2">{log.content}</p>

                      {log.context && Object.keys(log.context).length > 0 && (
                        <div className="text-xs text-gray-500">
                          <p className="font-medium mb-1">Context:</p>
                          <pre className="bg-gray-100 p-2 rounded text-xs overflow-x-auto">
                            {JSON.stringify(log.context, null, 2)}
                          </pre>
                        </div>
                      )}
                    </div>
                  );
                })}

                {filteredMemoryLogs.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    <Brain className="h-8 w-8 mx-auto mb-2 opacity-50" />
                    <p>No memory logs found</p>
                  </div>
                )}
              </div>
            </ScrollArea>
          </TabsContent>
        </Tabs>
      </CardContent>
    </Card>
  );
};

export default AgentHistoryView;

