import { useState, useEffect } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Slider } from "@/components/ui/slider";
import { Switch } from "@/components/ui/switch";
import { 
  Heart, 
  Zap, 
  Moon, 
  Dumbbell, 
  Brain,
  Save,
  CheckCircle,
  Calendar
} from "lucide-react";
import { motion } from "framer-motion";

export default function DailyLog() {
  const [mood, setMood] = useState([3]);
  const [energy, setEnergy] = useState([3]);
  const [sleep, setSleep] = useState([7]);
  const [exercise, setExercise] = useState(false);
  const [meditation, setMeditation] = useState(false);
  const [notes, setNotes] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);

  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get today's date for querying existing log
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const { data: todayLog } = useQuery({
    queryKey: ["/api/user/logs", { date: today.toISOString() }],
    retry: false,
    enabled: !!user,
  });

  // Load existing log data if available
  useEffect(() => {
    if (todayLog) {
      setMood([todayLog.mood || 3]);
      setEnergy([todayLog.energy || 3]);
      setSleep([todayLog.sleep || 7]);
      setExercise(todayLog.exercise || false);
      setMeditation(todayLog.meditation || false);
      setNotes(todayLog.notes || "");
      setIsSubmitted(true);
    }
  }, [todayLog]);

  const saveDailyLogMutation = useMutation({
    mutationFn: async (logData: any) => {
      const response = await apiRequest("POST", "/api/user/logs", logData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user/logs"] });
      setIsSubmitted(true);
      toast({
        title: "Daily Log Saved!",
        description: "Your wellness data has been recorded for today.",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "Please sign in to save your daily log.",
          variant: "destructive",
        });
        return;
      }
      toast({
        title: "Error",
        description: "Failed to save daily log. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    const logData = {
      date: new Date().toISOString(),
      mood: mood[0],
      energy: energy[0],
      sleep: sleep[0],
      exercise,
      meditation,
      notes: notes.trim() || null,
    };

    saveDailyLogMutation.mutate(logData);
  };

  const getMoodEmoji = (value: number) => {
    const emojis = ["üò¢", "üòï", "üòê", "üòä", "üòÑ"];
    return emojis[value - 1] || "üòê";
  };

  const getEnergyColor = (value: number) => {
    const colors = ["bg-red-500", "bg-orange-500", "bg-yellow-500", "bg-lime-500", "bg-green-500"];
    return colors[value - 1] || "bg-yellow-500";
  };

  return (
    <div className="space-y-6">
      {isSubmitted && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-green-50 border border-green-200 rounded-xl p-4 flex items-center gap-3"
        >
          <CheckCircle className="w-5 h-5 text-green-600" />
          <div>
            <p className="font-medium text-green-800">Today's log completed!</p>
            <p className="text-sm text-green-700">You can update it anytime throughout the day.</p>
          </div>
        </motion.div>
      )}

      <div className="grid md:grid-cols-2 gap-6">
        {/* Mood Tracking */}
        <Card className="organic-border">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Heart className="w-5 h-5 text-pink-600" />
              Mood
            </CardTitle>
            <CardDescription>How are you feeling today?</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="text-center">
                <div className="text-4xl mb-2">{getMoodEmoji(mood[0])}</div>
                <div className="text-lg font-semibold text-sage-700">
                  {mood[0]}/5
                </div>
              </div>
              <Slider
                value={mood}
                onValueChange={setMood}
                max={5}
                min={1}
                step={1}
                className="w-full"
              />
              <div className="flex justify-between text-sm text-sage-500">
                <span>Poor</span>
                <span>Excellent</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Energy Tracking */}
        <Card className="organic-border">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Zap className="w-5 h-5 text-yellow-600" />
              Energy Level
            </CardTitle>
            <CardDescription>How energetic do you feel?</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="text-center">
                <div className="flex justify-center mb-2">
                  {Array.from({ length: 5 }).map((_, i) => (
                    <div
                      key={i}
                      className={`w-4 h-8 mx-1 rounded-full ${
                        i < energy[0] ? getEnergyColor(energy[0]) : 'bg-sage-200'
                      }`}
                    />
                  ))}
                </div>
                <div className="text-lg font-semibold text-sage-700">
                  {energy[0]}/5
                </div>
              </div>
              <Slider
                value={energy}
                onValueChange={setEnergy}
                max={5}
                min={1}
                step={1}
                className="w-full"
              />
              <div className="flex justify-between text-sm text-sage-500">
                <span>Exhausted</span>
                <span>Energetic</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Sleep Tracking */}
        <Card className="organic-border">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Moon className="w-5 h-5 text-indigo-600" />
              Sleep
            </CardTitle>
            <CardDescription>Hours of sleep last night</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="text-center">
                <div className="text-3xl font-bold text-sage-700 mb-2">
                  {sleep[0]}h
                </div>
              </div>
              <Slider
                value={sleep}
                onValueChange={setSleep}
                max={12}
                min={3}
                step={0.5}
                className="w-full"
              />
              <div className="flex justify-between text-sm text-sage-500">
                <span>3h</span>
                <span>12h</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Activities */}
        <Card className="organic-border">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calendar className="w-5 h-5 text-sage-600" />
              Activities
            </CardTitle>
            <CardDescription>What did you do today?</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-6">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <Dumbbell className="w-5 h-5 text-blue-600" />
                  <div>
                    <Label htmlFor="exercise" className="text-sage-700 font-medium">
                      Exercise
                    </Label>
                    <p className="text-sm text-sage-600">Any physical activity</p>
                  </div>
                </div>
                <Switch
                  id="exercise"
                  checked={exercise}
                  onCheckedChange={setExercise}
                />
              </div>

              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <Brain className="w-5 h-5 text-purple-600" />
                  <div>
                    <Label htmlFor="meditation" className="text-sage-700 font-medium">
                      Meditation
                    </Label>
                    <p className="text-sm text-sage-600">Mindfulness or meditation</p>
                  </div>
                </div>
                <Switch
                  id="meditation"
                  checked={meditation}
                  onCheckedChange={setMeditation}
                />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Notes Section */}
      <Card className="organic-border">
        <CardHeader>
          <CardTitle>Daily Notes</CardTitle>
          <CardDescription>
            Any thoughts, reflections, or observations about your wellness today?
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Textarea
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            placeholder="How was your day? Any insights about your wellness journey?"
            rows={4}
            className="border-sage-200 focus:border-sage-500 focus:ring-sage-500"
          />
        </CardContent>
      </Card>

      {/* Save Button */}
      <div className="flex justify-center">
        <Button
          onClick={handleSave}
          disabled={saveDailyLogMutation.isPending}
          className="bg-sage-600 hover:bg-sage-700 text-white px-8 py-3"
        >
          {saveDailyLogMutation.isPending ? (
            "Saving..."
          ) : (
            <>
              <Save className="w-5 h-5 mr-2" />
              Save Daily Log
            </>
          )}
        </Button>
      </div>
    </div>
  );
}

client/src/components/wellness/product-c.content:
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Star, 
  ShoppingBag, 
  ExternalLink,
  Heart,
  Info
} from "lucide-react";
import { motion } from "framer-motion";

interface Product {
  id: number;
  name: string;
  description: string;
  price: string;
  category: string;
  tags?: string[];
  imageUrl?: string;
  affiliateLink?: string;
  rating?: string;
  isRecommended: boolean;
}

interface ProductCardProps {
  product: Product;
  onViewDetails?: () => void;
  onAddToWishlist?: () => void;
  showWishlist?: boolean;
}

export default function ProductCard({ 
  product, 
  onViewDetails,
  onAddToWishlist,
  showWishlist = true 
}: ProductCardProps) {
  const getCategoryColor = (category: string) => {
    switch (category.toLowerCase()) {
      case "supplements":
        return "bg-green-100 text-green-700";
      case "skincare":
        return "bg-pink-100 text-pink-700";
      case "fitness":
        return "bg-blue-100 text-blue-700";
      case "meditation":
        return "bg-purple-100 text-purple-700";
      case "nutrition":
        return "bg-orange-100 text-orange-700";
      case "sleep":
        return "bg-indigo-100 text-indigo-700";
      case "aromatherapy":
        return "bg-teal-100 text-teal-700";
      default:
        return "bg-sage-100 text-sage-700";
    }
  };

  const getPlaceholderImage = (category: string) => {
    switch (category.toLowerCase()) {
      case "supplements":
        return "https://images.unsplash.com/photo-1584017911766-d451b3d0e843?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300";
      case "skincare":
        return "https://images.unsplash.com/photo-1556228578-0d85b1a4d571?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300";
      case "fitness":
        return "https://images.unsplash.com/photo-1517836357463-d25dfeac3438?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300";
      case "meditation":
        return "https://images.unsplash.com/photo-1540206351-d6465b3ac5c1?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300";
      case "nutrition":
        return "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300";
      default:
        return "https://images.unsplash.com/photo-1584017911766-d451b3d0e843?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300";
    }
  };

  const handlePurchaseClick = () => {
    if (product.affiliateLink) {
      window.open(product.affiliateLink, '_blank', 'noopener,noreferrer');
    }
  };

  return (
    <motion.div
      whileHover={{ y: -5 }}
      transition={{ duration: 0.2 }}
    >
      <Card className="h-full organic-border premium-shadow overflow-hidden bg-white hover:shadow-lg transition-all duration-300">
        {/* Product Image */}
        <div className="relative aspect-video bg-gradient-to-r from-sage-100 to-sage-200">
          <img
            src={product.imageUrl || getPlaceholderImage(product.category)}
            alt={product.name}
            className="w-full h-full object-cover"
            onError={(e) => {
              (e.target as HTMLImageElement).src = getPlaceholderImage(product.category);
            }}
          />

          {/* Wishlist Button */}
          {showWishlist && (
            <Button
              variant="ghost"
              size="sm"
              className="absolute top-2 right-2 bg-white/80 hover:bg-white"
              onClick={onAddToWishlist}
            >
              <Heart className="w-4 h-4 text-sage-600" />
            </Button>
          )}

          {/* Recommended Badge */}
          {product.isRecommended && (
            <Badge className="absolute top-2 left-2 bg-amber-500 text-white">
              Editor's Pick
            </Badge>
          )}
        </div>

        <CardHeader>
          <div className="flex items-center justify-between mb-2">
            <Badge className={getCategoryColor(product.category)}>
              {product.category}
            </Badge>
            {product.rating && (
              <div className="flex items-center space-x-1">
                <Star className="w-4 h-4 text-amber-400 fill-current" />
                <span className="text-sm text-sage-600">{product.rating}</span>
              </div>
            )}
          </div>

          <CardTitle className="text-sage-800 leading-tight line-clamp-2">
            {product.name}
          </CardTitle>
          <CardDescription className="text-sage-600 line-clamp-3">
            {product.description}
          </CardDescription>
        </CardHeader>

        <CardContent>
          <div className="space-y-4">
            {/* Tags */}
            {product.tags && product.tags.length > 0 && (
              <div className="flex flex-wrap gap-1">
                {product.tags.slice(0, 3).map((tag, index) => (
                  <Badge key={index} variant="outline" className="text-xs">
                    {tag}
                  </Badge>
                ))}
                {product.tags.length > 3 && (
                  <Badge variant="outline" className="text-xs">
                    +{product.tags.length - 3}
                  </Badge>
                )}
              </div>
            )}

            {/* Price */}
            <div className="flex items-center justify-between">
              <span className="text-2xl font-bold text-sage-800">
                ¬£{product.price}
              </span>
              <div className="text-right">
                <div className="text-xs text-sage-500">Starting from</div>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="space-y-2">
              {product.affiliateLink ? (
                <Button 
                  className="w-full bg-sage-600 hover:bg-sage-700 text-white"
                  onClick={handlePurchaseClick}
                >
                  <ShoppingBag className="w-4 h-4 mr-2" />
                  Shop Now
                  <ExternalLink className="w-4 h-4 ml-2" />
                </Button>
              ) : (
                <Button 
                  className="w-full bg-sage-600 hover:bg-sage-700 text-white"
                  disabled
                >
                  <ShoppingBag className="w-4 h-4 mr-2" />
                  Coming Soon
                </Button>
              )}

              <Button 
                variant="outline" 
                className="w-full border-sage-200 text-sage-600 hover:bg-sage-50"
                onClick={onViewDetails}
              >
                <Info className="w-4 h-4 mr-2" />
                Learn More
              </Button>
            </div>

            {/* Additional Info */}
            <div className="pt-2 border-t border-sage-100">
              <div className="flex items-center justify-between text-xs text-sage-500">
                <span>Free shipping on orders over ¬£50</span>
                <span>30-day return policy</span>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </motion.div>
  );
}

